import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

// Function to determine skill level category (Beginner, Proficient, Master)
const getSkillCategory = (level) => {
  if (level <= 40) return "Beginner";
  if (level <= 75) return "Proficient";
  return "Master";
};

// Function to generate smooth color gradient from red (low skill) to green (high skill)
const getSkillColor = (level) => {
  const red = Math.max(255 - level * 2.55, 0);
  const green = Math.min(level * 2.55, 255);
  return `rgb(${red}, ${green}, 80)`;
};

// Function to determine ring thickness & glow effect
const getRingStyle = (level) => {
  const category = getSkillCategory(level);
  return {
    strokeWidth: category === "Beginner" ? 3 : category === "Proficient" ? 5 : 7,
    filter: category === "Master" ? "drop-shadow(0 0 10px rgba(0,255,0,0.8))" : 
           category === "Proficient" ? "drop-shadow(0 0 5px rgba(255,255,0,0.6))" : 
           "drop-shadow(0 0 3px rgba(255,0,0,0.5))",
  };
};

export default function SkillItem({ skillName, skillLevel }) {
  const [isHovered, setIsHovered] = useState(false);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });

  const category = getSkillCategory(skillLevel);
  const ringStyle = getRingStyle(skillLevel);

  // Animate the progress ring (only when hovered)
  const progressSpring = useSpring({
    strokeDashoffset: isHovered ? 251 - (251 * skillLevel) / 100 : 251, // Full ring = 251
    opacity: isHovered ? 1 : 0, // Hide when not hovered
    config: { tension: 200, friction: 20 },
  });

  return (
    <div
      className="relative flex items-center justify-center"
      onMouseEnter={() => setIsHovered(true)}
      onMouseMove={(e) => setMousePos({ x: e.clientX, y: e.clientY })}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Outer Progress Ring (Appears on Hover) */}
      {isHovered && (
        <svg className="absolute w-24 h-24" viewBox="0 0 100 100">
          {/* Background Track */}
          <circle
            cx="50"
            cy="50"
            r="45"
            stroke="rgba(255, 255, 255, 0.2)"
            strokeWidth="5"
            fill="none"
          />
          {/* Animated Progress Ring */}
          <animated.circle
            cx="50"
            cy="50"
            r="45"
            stroke={getSkillColor(skillLevel)}
            strokeWidth={ringStyle.strokeWidth}
            fill="none"
            strokeDasharray="251"
            strokeDashoffset={progressSpring.strokeDashoffset}
            strokeLinecap="round"
            style={{ opacity: progressSpring.opacity, filter: ringStyle.filter }}
          />
        </svg>
      )}

      {/* Skill Icon */}
      <div className="w-16 h-16 sm:w-20 sm:h-20 flex items-center justify-center rounded-full bg-gray-700 shadow-lg">
        {skillIcons[skillName] || <span className="text-white">{skillName}</span>}
      </div>

      {/* Skill Name Tooltip (Follows Cursor) */}
      {isHovered && (
        <div
          className="fixed bg-gray-800 text-white text-sm font-semibold px-3 py-1 rounded-lg shadow-lg pointer-events-none"
          style={{
            top: `${mousePos.y + 10}px`,
            left: `${mousePos.x + 15}px`,
            whiteSpace: "nowrap",
          }}
        >
          {skillName} - {category}
        </div>
      )}
    </div>
  );
}
