import React, { useState, useRef, useEffect } from "react";
import Chatbot from "./chatbot.js";
import "./styles.css";

export default function About() {
  const [chatOpened, setChatOpened] = useState(false);
  const chatbotWrapperRef = useRef(null);

  useEffect(() => {
    const container = chatbotWrapperRef.current;
    if (container) {
      const handleClick = (e) => {
        if (e.target.closest(".chat-button")) {
          setChatOpened(true);
        } else if (e.target.closest(".close-btn")) {
          setChatOpened(false);
        }
      };
      container.addEventListener("click", handleClick);
      return () => container.removeEventListener("click", handleClick);
    }
  }, []);

  return (
    <section
      id="about"
      className="px-5 md:px-10 py-10 bg-purple-100"
      /* Optionally, use a full-screen approach:
       * className="px-5 md:px-10 min-h-screen flex items-center bg-purple-100"
       */
    >
      <div
        className="
          container mx-auto
          min-h-[600px]               /* Ensures enough vertical height to center */
          flex flex-col md:flex-row
          items-center justify-center /* Horizontal + vertical centering in the row */
          gap-8
        "
      >
        {/* LEFT COLUMN: Chat/Text */}
        <div
          className="
            w-full md:w-1/2
            flex flex-col items-center justify-center
            text-center h-full
          "
        >
          {!chatOpened && (
            <p className="text-gray-700 text-lg md:text-xl font-semibold leading-relaxed mb-4">
              Hello! My name is Annie. Have any questions?
            </p>
          )}
          <div ref={chatbotWrapperRef} className="w-full max-w-md mx-auto">
            <Chatbot />
          </div>
        </div>

        {/* RIGHT COLUMN: Image */}
        <div className="w-full md:w-1/2 max-w-xs md:max-w-sm lg:max-w-md aspect-square overflow-hidden rounded-xl shadow-lg">
          <img
            src="/Headshot.JPG"
            alt="Headshot"
            className="object-cover w-full h-full"
          />
        </div>
      </div>
    </section>
  );
}
