import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

// Function to determine skill level category
const getSkillCategory = (level) => {
  if (level <= 40) return "Beginner";
  if (level <= 75) return "Proficient";
  return "Master";
};

// Function to generate smooth color gradient from red (low skill) to green (high skill)
const getSkillColor = (level) => {
  const red = Math.max(255 - level * 2.55, 0);
  const green = Math.min(level * 2.55, 255);
  return `rgb(${red}, ${green}, 80)`;
};

// Function to determine the size of the glow based on skill level
const getGlowSize = (level) => {
  if (level <= 40) return "drop-shadow(0 0 10px rgba(255, 0, 0, 0.6))"; // Beginner: Small Red Glow
  if (level <= 75) return "drop-shadow(0 0 15px rgba(255, 255, 0, 0.7))"; // Proficient: Medium Yellow Glow
  return "drop-shadow(0 0 20px rgba(0, 255, 0, 0.9))"; // Master: Large Green Glow
};

export default function SkillItem({ skillName, skillLevel }) {
  const [isHovered, setIsHovered] = useState(false);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });

  const category = getSkillCategory(skillLevel);
  const skillColor = getSkillColor(skillLevel);
  const glowEffect = getGlowSize(skillLevel);

  // Animate the glow effect on hover
  const glowSpring = useSpring({
    opacity: isHovered ? 1 : 0,
    transform: isHovered ? "scale(1.3)" : "scale(1)",
    config: { tension: 200, friction: 20 },
  });

  return (
    <div
      className="relative flex items-center justify-center"
      onMouseEnter={() => setIsHovered(true)}
      onMouseMove={(e) => setMousePos({ x: e.clientX, y: e.clientY })}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Glowing Halo Effect (Appears on Hover) */}
      <animated.div
        className="absolute w-24 h-24 rounded-full"
        style={{
          backgroundColor: skillColor,
          filter: glowEffect,
          opacity: glowSpring.opacity,
          transform: glowSpring.transform,
        }}
      />

      {/* Skill Icon */}
      <div className="w-16 h-16 sm:w-20 sm:h-20 flex items-center justify-center rounded-full bg-gray-700 shadow-lg">
        {skillIcons[skillName] || <span className="text-white">{skillName}</span>}
      </div>

      {/* Skill Name Tooltip (Follows Cursor) */}
      {isHovered && (
        <div
          className="fixed bg-gray-900 text-white text-sm font-semibold px-3 py-1 rounded-lg shadow-lg pointer-events-none"
          style={{
            top: `${mousePos.y + 10}px`,
            left: `${mousePos.x + 15}px`,
            whiteSpace: "nowrap",
          }}
        >
          {skillName} ({category})
        </div>
      )}
    </div>
  );
}
