import React, { useState } from "react";
import { ChipIcon } from "@heroicons/react/solid";
import { skills } from "../data"; // Import skills data
import SkillItem from "./SkillItem"; // Ensure correct case for component import
import "../styles/skills.css"; // Import CSS styles

export default function Skills() {
  const categories = Object.keys(skills);
  const [selectedCategory, setSelectedCategory] = useState(categories[0]);

  return (
    <section id="skills" className="relative bg-gray-900 text-center py-14">
      <div className="container px-5 mx-auto">
        {/* Section Title */}
        <div className="mb-10">
          <ChipIcon className="w-10 mx-auto mb-4 text-purple-300" />
          <h1 className="sm:text-4xl text-3xl font-medium title-font text-white">
            Tech Stack & Skills
          </h1>
        </div>

        {/* Category Tabs */}
        <div className="flex justify-center mb-8 space-x-4">
          {categories.map((category) => (
            <button
              key={category}
              onClick={() => setSelectedCategory(category)}
              className={`px-5 py-2 font-medium rounded-lg ${
                selectedCategory === category
                  ? "bg-purple-200 text-gray-600"
                  : "bg-gray-700 text-gray-300 hover:bg-gray-500"
              }`}
            >
              {category}
            </button>
          ))}
        </div>

        {/* Skills Grid */}
        <div className="skill-container">
          {skills[selectedCategory].map((skill) => (
            <div key={skill} className="skill-item group relative">
              <SkillItem skill={skill} /> {/* Use SkillItem component */}
              <span className="absolute bottom-0 mb-6 px-2 py-1 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                {skill} {/* Show skill name on hover */}
              </span>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
