import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

const levelToColor = {
    Beginner: "#F44336", // Red
    Proficient: "#FFC107", // Yellow
    Mastery: "#4CAF50", // Green
  };

  export default function SkillsContainer() {
    return (
      <section id="skills" className="bg-gray-900 py-14">
        <div className="container mx-auto px-5 text-center">
          {/* Title */}
          <h1 className="text-3xl sm:text-4xl font-medium text-white mb-10">
            Tech Stack & Skills
          </h1>

          {/* Skill Categories Container */}
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
            {Object.entries(skills).map(([category, skillList]) => (
              <SkillCategory key={category} category={category} skillList={skillList} />
            ))}
          </div>
        </div>
      </section>
    );
  }

  // Skill Category Component
  function SkillCategory({ category, skillList }) {
    return (
      <div className="bg-gray-800 rounded-xl p-6 shadow-lg">
        <h2 className="text-xl font-semibold text-white mb-4">{category}</h2>

        {/* Skills Grid */}
        <div className="grid grid-cols-3 sm:grid-cols-4 gap-6 justify-center">
          {skillList.map((skill, index) => (
            <SkillItem key={`${category}-${index}`} skill={skill} />
          ))}
        </div>
      </div>
    );
  }

  // Skill Item Component
  function SkillItem({ skill }) {
    const { name, level } = skill;
    const [isHovered, setIsHovered] = useState(false);

    // Enlarge animation on hover
    const iconSpring = useSpring({
      transform: isHovered ? "scale(1.3)" : "scale(1)",
      config: { tension: 200, friction: 15 },
    });

    return (
      <div
        className="flex flex-col items-center justify-center relative transition-all duration-300"
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        {/* Enlarging Circular Skill Icon */}
        <animated.div
          style={iconSpring}
          className="w-16 h-16 sm:w-20 sm:h-20 flex items-center justify-center rounded-full bg-gray-700 transition-all duration-300"
        >
          {skillIcons[name] || <span className="text-white">{name}</span>}
        </animated.div>

        {/* Skill Level Label (Appears on Hover) */}
        {isHovered && (
          <span
            className="mt-2 text-sm font-medium px-2 py-1 rounded-md text-white"
            style={{ backgroundColor: levelToColor[level] }}
          >
            {level}
          </span>
        )}
      </div>
    );
  }
