//project.js
import { projects } from "../data";
import {
  FaReact, FaGithub, FaUnity, FaVideo, FaNodeJs, FaSwift, FaAws, FaJava, FaAngular
} from "react-icons/fa";
import { FaMeta } from "react-icons/fa6";
import {
  SiDjango, SiMysql, SiBlender, SiUnrealengine, SiMongodb, SiPostman, SiPython, SiFastapi
} from "react-icons/si";
import { VscAzureDevops } from "react-icons/vsc";
import { RiTailwindCssFill } from "react-icons/ri";
import { GrHeroku } from "react-icons/gr";
import { IoLogoVercel } from "react-icons/io5";
import { AiOutlineOpenAI } from "react-icons/ai";
import React, { useState, useEffect, useRef } from "react";
import "./projects.css"

const DynamicText = ({ text }) => {
  const [fontSize, setFontSize] = useState("text-lg");
  const textRef = useRef(null);
  useEffect(() => {
    if (textRef.current) {
      const length = text.length;
      if (length > 200) setFontSize("text-sm");
      else if (length > 100) setFontSize("text-base");
      else setFontSize("text-lg");
    }
  }, [text]);
  return (
    <p ref={textRef} className={`text-purple-200 ${fontSize} text-center leading-snug`}>
      {text}
    </p>
  );
};

const iconComponents = {
  FaReact, SiDjango, SiMysql, SiBlender, SiUnrealengine, FaUnity, FaMeta,
  FaSwift, FaNodeJs, RiTailwindCssFill, GrHeroku, AiOutlineOpenAI,
  IoLogoVercel, FaAws, VscAzureDevops, SiMongodb, SiPostman, FaJava,
  SiPython, SiFastapi, FaAngular
};

const renderIcon = (iconName) => {
  const IconComponent = iconComponents[iconName];
  return IconComponent ? <IconComponent className="text-4xl mx-2 text-purple-200" /> : null;
};

const ProjectCard = ({ project, animateOnLoad, index }) => {
  const [isFlipped, setIsFlipped] = useState(false);

  useEffect(() => {
    if (animateOnLoad) {
      const delay = index * 200;
      setTimeout(() => {
        setIsFlipped(true);
        setTimeout(() => setIsFlipped(false), 800);
      }, delay);
    }
  }, [animateOnLoad, index]);

  return (
    <div 
      className="flip-card relative w-full h-[400px] cursor-pointer group" 
      onClick={() => setIsFlipped(!isFlipped)}
    >
      <div className={`flip-card-inner w-full h-full ${isFlipped ? "rotate-y-180" : ""}`}>
        {/* Front of the card */}
        <div className="flip-card-front absolute w-full h-full p-4 bg-gray-800 rounded-lg shadow-lg flex flex-col justify-between z-20">
          <div>
            <h2 className="text-lg font-bold text-purple-100">{project.title}</h2>
          </div>
          <div className="flex-grow flex items-center justify-center text-center">
            <DynamicText text={project.description} />
          </div>
          {project.technologies && (
            <div className="mt-auto p-4 bg-gray-900 rounded-lg flex items-center justify-center">
              <div className="flex space-x-4">
                {project.technologies.map((tech) => (
                  <div key={tech.icon} className="p-2 flex items-center justify-center">
                    {renderIcon(tech.icon)}
                  </div>
                ))}
              </div>
            </div>
          )}
          {project.link && (
            <a 
              href={project.link} 
              target="_blank" 
              rel="noopener noreferrer" 
              className="text-purple-400 hover:text-white mt-4 inline-block"
            >
              View Project
            </a>
          )}
          {project.github && (
            <a 
              href={project.github} 
              target="_blank" 
              rel="noopener noreferrer" 
              className="absolute top-4 right-4 text-purple-300 hover:text-white"
            >
              <FaGithub className="text-2xl" />
            </a>
          )}
        </div>
        {/* Back of the card â€“ Minimalistic and Modern */}
        <div className="flip-card-back absolute w-full h-full p-4 bg-gray-800 rounded-lg shadow-lg flex items-center justify-center z-10">
          <div className="flex flex-col items-center justify-center space-y-4 w-full h-full">
            {project.subtitle && (
              <h3 className="text-2xl text-purple-100 font-bold text-center">
                {project.subtitle}
              </h3>
            )}
            <hr className="w-1/2 border-t border-gray-700" />
            {project.technologies && (
              <div className="flex flex-wrap justify-center gap-4">
                {project.technologies.map((tech) => (
                  <div key={tech.icon} className="flex flex-col items-center">
                    {renderIcon(tech.icon)}
                    <span className="text-xs text-purple-200">{tech.name}</span>
                  </div>
                ))}
              </div>
            )}
            <div className="flex gap-4 mt-4">
              {project.link && (
                <a 
                  href={project.link} 
                  target="_blank" 
                  rel="noopener noreferrer" 
                  className="text-purple-400 hover:text-white"
                >
                  Live Demo
                </a>
              )}
              {project.github && (
                <a 
                  href={project.github} 
                  target="_blank" 
                  rel="noopener noreferrer" 
                  className="text-purple-400 hover:text-white"
                >
                  <FaGithub className="text-2xl" />
                </a>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default function Projects() {
  const [activeTab, setActiveTab] = useState("completed");
  const [triggerAnimation, setTriggerAnimation] = useState(false);

  const handleTabChange = (tab) => {
    setActiveTab(tab);
    setTriggerAnimation(false);
    setTimeout(() => setTriggerAnimation(true), 100);
  };

  const completedProjects = projects.filter(p => p.status === "completed");
  const inDevelopmentProjects = projects.filter(p => p.status === "in-development");
  const schoolCodeExamples = projects.filter(p => p.category === "School");

  const renderSchoolProject = (project) => (
    <div key={project.id} className="p-6 bg-gray-800 rounded-lg shadow-lg transform transition-transform hover:scale-105 hover:shadow-2xl flex flex-col justify-between h-full min-h-[250px]">
      <h2 className="text-lg font-bold text-purple-100 text-center mb-4">{project.title}</h2>
      <div className="flex-grow flex items-center justify-center text-center">
        <DynamicText text={project.description} />
      </div>
      {project.github && (
        <a href={project.github} target="_blank" rel="noopener noreferrer" className="absolute top-4 right-4 text-purple-300 hover:text-white">
          <FaGithub className="text-2xl" />
        </a>
      )}
    </div>
  );

  return (
    <section id="projects" className="text-gray-800 bg-purple-100 body-font">
      <div className="container px-4 py-10 mx-auto text-center lg:px-20">
        <div className="flex justify-center mb-8">
          <button className={`px-4 py-2 font-bold ${activeTab === "completed" ? "bg-gray-800 text-purple-100" : "bg-purple-100 text-gray-800"} rounded-lg mr-2`} onClick={() => handleTabChange("completed")}>Completed Projects</button>
          <button className={`px-4 py-2 font-bold ${activeTab === "in-development" ? "bg-gray-800 text-purple-100" : "bg-purple-100 text-gray-800"} rounded-lg mr-2`} onClick={() => handleTabChange("in-development")}>Projects in Development</button>
          <button className={`px-4 py-2 font-bold ${activeTab === "school" ? "bg-gray-800 text-purple-100" : "bg-purple-100 text-gray-800"} rounded-lg`} onClick={() => handleTabChange("school")}>School Projects</button>
        </div>

        {activeTab === "completed" && (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 items-stretch">
            {completedProjects.map((project, index) => <ProjectCard key={project.id} project={project} animateOnLoad={triggerAnimation} index={index} />)}
          </div>
        )}

        {activeTab === "in-development" && (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {inDevelopmentProjects.map((project, index) => <ProjectCard key={project.id} project={project} animateOnLoad={triggerAnimation} index={index} />)}
          </div>
        )}

        {activeTab === "school" && (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {schoolCodeExamples.map((project, index) => renderSchoolProject(project))}
          </div>
        )}
      </div>
    </section>
  );
}
