import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

const levelToPercentage = {
  Beginner: 40,
  Proficient: 75,
  Mastery: 100,
};

const levelToColor = {
  Beginner: "#F44336", // Red
  Proficient: "#FFC107", // Yellow
  Mastery: "#4CAF50", // Green
};

export default function SkillItem({ skillName, skillLevel }) {
  const [isHovered, setIsHovered] = useState(false);

  // Animation for progress bar fill
  const progressSpring = useSpring({
    width: isHovered ? `${levelToPercentage[skillLevel]}%` : "0%",
    config: { tension: 200, friction: 15 },
  });

  return (
    <div
      className="flex flex-col items-center p-4 bg-gray-800 rounded-lg shadow-lg transition-transform duration-300 hover:scale-105 w-40"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Skill Icon */}
      <div className="flex items-center justify-center w-16 h-16 bg-gray-700 rounded-full mb-3">
        {skillIcons[skillName] || <span className="text-white">{skillName}</span>}
      </div>

      {/* Skill Name */}
      <span className="text-white text-lg font-semibold">{skillName}</span>

      {/* Progress Bar (Only Visible on Hover) */}
      <div className="w-full bg-gray-600 h-2 rounded-md mt-3 relative overflow-hidden">
        <animated.div
          className="h-full rounded-md"
          style={{
            backgroundColor: levelToColor[skillLevel],
            ...progressSpring,
          }}
        />
      </div>
    </div>
  );
}
