import React, { useState } from "react";
import { motion } from "framer-motion";
import Slider from "react-slick";
import { skills } from "../data"; // Import skills data
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import skillIcons from "./skillIcons"; // Ensure you have icons mapped

export default function SkillCarousel() {
  const [selectedCategory, setSelectedCategory] = useState(Object.keys(skills)[0]);

  const settings = {
    infinite: true,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
    pauseOnHover: true
  };

  return (
    <div className="flex flex-col items-center w-full max-w-3xl mx-auto">
      {/* Category Tabs */}
      <div className="flex flex-wrap justify-center space-x-3 mb-6">
        {Object.keys(skills).map((category) => (
          <button
            key={category}
            onClick={() => setSelectedCategory(category)}
            className={`px-4 py-2 font-medium rounded-lg transition duration-300 ${
              selectedCategory === category ? "bg-blue-500 text-white" : "bg-gray-700 text-gray-300"
            } hover:bg-gray-500`}
          >
            {category}
          </button>
        ))}
      </div>

      {/* Skills Carousel (Rotating Icons) */}
      <motion.div
        className="w-full"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <Slider {...settings}>
          {skills[selectedCategory].map((skill, index) => (
            <div key={index} className="flex flex-col items-center justify-center">
              <div className="w-16 h-16 flex items-center justify-center rounded-full bg-gray-800 shadow-lg">
                {skillIcons[skill.name] || <span className="text-white">{skill}</span>}
              </div>
              <span className="mt-2 text-white text-sm">{skill}</span>
            </div>
          ))}
        </Slider>
      </motion.div>
    </div>
  );
}
