import React, { useState, useEffect, useRef } from "react";
import "./chatbot.css";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [isOpen, setIsOpen] = useState(false);
  const [streamingResponse, setStreamingResponse] = useState("");
  const eventSourceRef = useRef(null); 
  const chatContainerRef = useRef(null);
  const endOfMessagesRef = useRef(null);

  // Helper function: scroll the dummy element into view.


  const scrollChatContainer = () => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  };


  // useEffect to scroll after each messages update
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    console.log("Starting chatbot request...");
    const userMessage = { role: "user", content: input };
    setMessages((prev) => [...prev, userMessage]);
    setInput("");
    setStreamingResponse("");
    setIsTyping(true);

    try {
      const API_BASE_URL = "https://chatbotannie-production.up.railway.app"; 
      const API_DEV_URL = ""
      const response = await fetch(`${API_BASE_URL}/chat`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: input, session_id: "session_123" }),
      });
      const startReqTime = performance.now();
      const endReqTime = performance.now();
      console.log(`API request completed in ${(endReqTime - startReqTime).toFixed(4)} ms`);

      const reader = response.body.getReader();
      const decoder = new TextDecoder("utf-8");
      let botMessage = { role: "bot", content: "" };
      let firstChunk = true;
      setMessages((prev) => [...prev, botMessage]);

      let buffer = "";
      const updateInterval = 100; // update every 100ms
      let lastUpdateTime = Date.now();
      const startStreamTime = performance.now();

      while (true) {
        const { value, done } = await reader.read();
        if (done) break;

        const chunk = decoder.decode(value, { stream: true });
        buffer += chunk;
        const now = Date.now();

        // Update state if enough time has passed or on first chunk
        if (firstChunk || now - lastUpdateTime > updateInterval) {
          botMessage.content += buffer;
          setMessages((prev) => [...prev.slice(0, -1), { ...botMessage }]);
          buffer = "";
          lastUpdateTime = now;
          if (firstChunk) {
            firstChunk = false;
            setIsTyping(false);
          }
          // Scroll only the chat container, not the entire page.
          setTimeout(scrollChatContainer, 10);
        }
      }


      if (buffer.length > 0) {
        botMessage.content += buffer;
        setMessages((prev) => [...prev.slice(0, -1), { ...botMessage }]);
        setTimeout(scrollToBottom, 10);
      }

      const endStreamTime = performance.now();
      console.log(`Streaming took ${(endStreamTime - startStreamTime).toFixed(4)} ms`);
      console.log(`Total chatbot response time: ${(endStreamTime - startReqTime).toFixed(4)} ms`);
    } catch (error) {
      console.error("Error:", error);
    } finally {
      setIsTyping(false);
    }
  };

  const clearMessages = () => {
    setMessages([]);
    setStreamingResponse("");
    setIsTyping(false);
    if (eventSourceRef.current) {
      eventSourceRef.current.close();
      eventSourceRef.current = null;
    }
  };

  return (
    <div className="chatbot-wrapper" style={{ width: "350px", position: "relative" }}>
      <button
        className={`chat-button ${isOpen ? "hide-chat" : "show-chat"}`}
        onClick={() => setIsOpen(true)}
      >
        Chat with me
      </button>
      <div className={`chat-container ${isOpen ? "show-chat" : "hide-chat"}`}>
        <div className="chat-header">
          <h2>Chat with me</h2>
          <button className="close-btn" onClick={() => setIsOpen(false)}>
            âœ–
          </button>
        </div>
        {/* Set a fixed height and overflow for auto-scrolling */}
        <div
          className="chatbox"
          ref={chatContainerRef}
          style={{ height: "400px", overflowY: "auto", padding: "10px" }}
        >
          {messages.map((msg, index) => (
            <div
              key={index}
              className={msg.role === "user" ? "user-message" : "bot-message"}
            >
              {msg.content}
            </div>
          ))}
          {/* Dummy element for scrolling */}
          <div ref={endOfMessagesRef} />
          {isTyping && (
            <div className="bot-typing">
              <div className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          )}
        </div>

        <div
          className="chat-input"
          style={{ display: "flex", padding: "10px", backgroundColor: "#111827" }}
        >
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === "Enter") {
                sendMessage();
                e.preventDefault();
              }
            }}
            placeholder="Ask me anything..."
            style={{ flex: 1, marginRight: "5px" }}
          />
          <button className="send" onClick={sendMessage}>
            Send
          </button>
          <button className="clear" onClick={clearMessages}>
            Clear
          </button>
        </div>
      </div>
    </div>
  );
};

export default Chatbot;
