import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

// Skill experience levels mapped to stroke percentages
const levelToPercentage = {
  Beginner: 40,
  Proficient: 75,
  Mastery: 100,
};

// Skill level colors for glow & progress stroke
const levelToColor = {
  Beginner: "#F44336", // Red
  Proficient: "#FFC107", // Yellow
  Mastery: "#4CAF50", // Green
};

export default function SkillItem({ skill }) {
  const { name, level } = skill || {};
  const [isHovered, setIsHovered] = useState(false);

  // Animated stroke effect for progress ring
  const progressSpring = useSpring({
    strokeDashoffset: 251 - (251 * levelToPercentage[level]) / 100,
    config: { tension: 200, friction: 15 },
  });

  // Scale effect on hover
  const iconSpring = useSpring({
    transform: isHovered ? "scale(1.2)" : "scale(1)",
    config: { tension: 200, friction: 15 },
  });

  return (
    <div
      className="group flex flex-col items-center justify-center w-28 h-28 relative"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Circular Progress Ring */}
      <svg className="absolute w-28 h-28" viewBox="0 0 100 100">
        <circle
          cx="50"
          cy="50"
          r="40"
          stroke="#333"
          strokeWidth="6"
          fill="none"
        />
        <animated.circle
          cx="50"
          cy="50"
          r="40"
          stroke={levelToColor[level] || "#F44336"}
          strokeWidth="6"
          fill="none"
          strokeLinecap="round"
          strokeDasharray="251"
          strokeDashoffset={progressSpring.strokeDashoffset}
          className="transition-all duration-500"
        />
      </svg>

      {/* Circular Skill Icon with Glow Effect */}
      <div
        className={`relative w-24 h-24 flex items-center justify-center rounded-full bg-gray-700 transition-all duration-300`}
        style={{
          boxShadow: isHovered ? `0 0 15px ${levelToColor[level]}` : "none",
        }}
      >
        <animated.div style={iconSpring}>
          {skillIcons[name] || <span className="text-white">{name}</span>}
        </animated.div>
      </div>

      {/* Skill Name (Always Visible) */}
      <span className="mt-2 text-white text-sm font-semibold">{name}</span>

      {/* Experience Level Label (Only on Hover) */}
      <animated.span
        style={{ opacity: isHovered ? 1 : 0 }}
        className={`text-xs font-medium mt-1 transition-opacity duration-300 ${
          level === "Mastery"
            ? "text-green-400"
            : level === "Proficient"
            ? "text-yellow-400"
            : "text-red-400"
        }`}
      >
        {level}
      </animated.span>
    </div>
  );
}
