import React, { useState, useMemo } from "react";
import { FaGithub } from "react-icons/fa";
import { projects } from "../data"; // or wherever your projects data is
import "./projects.css"; // We'll include a CSS snippet below

function ProjectCard({ project }) {
  const [isFlipped, setIsFlipped] = useState(false);

  const handleFlip = () => {
    setIsFlipped((prev) => !prev);
  };

  return (
    <div className="flip-card" onClick={handleFlip}>
      <div className={`flip-card-inner ${isFlipped ? "flipped" : ""}`}>

        {/* FRONT */}
        <div className="flip-card-front bg-[#1f2937] rounded-lg shadow-lg flex flex-col justify-center items-center text-center p-6">
          <h2 className="text-2xl font-bold text-purple-100">{project.title}</h2>
          {project.subtitle && (
            <p className="text-sm text-purple-300 mt-2">{project.subtitle}</p>
          )}
        </div>

        {/* BACK */}
        <div className="flip-card-back bg-[#1f2937] rounded-lg shadow-lg text-purple-100 p-6 flex flex-col">
          {/* Description */}
          <p className="text-sm leading-relaxed mb-4">{project.description}</p>

          {/* Functions */}
          {project.functions && project.functions.length > 0 && (
            <div className="mb-4">
              <h4 className="font-semibold text-sm mb-1">Main Functions</h4>
              <ul className="list-disc list-inside text-sm text-left">
                {project.functions.map((func, idx) => (
                  <li key={idx}>{func}</li>
                ))}
              </ul>
            </div>
          )}

          {/* Technologies */}
          {project.technologies && project.technologies.length > 0 && (
            <div className="flex flex-wrap gap-2 mb-4">
              {project.technologies.map((tech) => (
                <span
                  key={tech.name}
                  className="bg-purple-200 text-gray-900 px-2 py-1 rounded text-xs font-medium"
                >
                  {tech.name}
                </span>
              ))}
            </div>
          )}

          {/* Links */}
          <div className="mt-auto flex gap-4 justify-center pt-2">
            {project.github && (
              <a
                href={project.github}
                target="_blank"
                rel="noopener noreferrer"
                className="text-purple-200 hover:text-white text-md inline-flex items-center"
                onClick={(e) => e.stopPropagation()} 
                // Prevent flipping when link is clicked
              >
                <FaGithub className="mr-1" /> Code
              </a>
            )}
            {project.link && (
              <a
                href={project.link}
                target="_blank"
                rel="noopener noreferrer"
                className="text-purple-200 hover:text-white text-md"
                onClick={(e) => e.stopPropagation()} 
                // Prevent flipping when link is clicked
              >
                Live
              </a>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default function Projects() {
  const [activeTab, setActiveTab] = useState("completed");

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  const completedProjects = projects.filter((p) => p.status === "completed");
  const schoolCodeExamples = projects.filter((p) => p.category === "School");

  // Shuffle the completed projects if desired
  const shuffledCompletedProjects = useMemo(() => {
    const shuffled = [...completedProjects];
    for (let i = shuffled.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
    }
    return shuffled;
  }, [completedProjects]);

  return (
    <section id="projects" className="text-purple-100 bg-gray-900 body-font">
      <div className="container px-4 py-10 mx-auto text-left lg:px-20">

        {/* Tabs */}
        <div className="flex justify-center mb-10">
          <button
            className={`px-4 py-2 font-bold rounded-lg mr-2 ${
              activeTab === "completed"
                ? "bg-purple-200 text-gray-700"
                : "bg-gray-800 text-purple-100"
            }`}
            onClick={() => handleTabChange("completed")}
          >
            Completed Projects
          </button>
          <button
            className={`px-4 py-2 font-bold rounded-lg ${
              activeTab === "school"
                ? "bg-purple-200 text-gray-700"
                : "bg-gray-800 text-purple-100"
            }`}
            onClick={() => handleTabChange("school")}
          >
            School Projects
          </button>
        </div>

        {/* Completed Projects Grid */}
        {activeTab === "completed" && (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
            {shuffledCompletedProjects.map((project) => (
              <ProjectCard key={project.title} project={project} />
            ))}
          </div>
        )}

        {/* School Projects Grid */}
        {activeTab === "school" && (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 mt-10">
            {schoolCodeExamples.map((project) => (
              <div
                key={project.title}
                className="p-6 bg-gray-800 rounded-lg shadow-lg"
              >
                <h2 className="text-lg font-bold text-purple-100 text-center mb-4">
                  {project.title}
                </h2>
                <p className="text-sm text-purple-200">{project.description}</p>
                {project.github && (
                  <a
                    href={project.github}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="block mt-4 text-purple-300 hover:text-white"
                  >
                    <FaGithub className="inline-block mr-1" /> View Code
                  </a>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </section>
  );
}
