import React, { useState, useMemo } from "react";
import { ChipIcon } from "@heroicons/react/solid";
import { skills } from "../data"; // Importing skills
import skillIcons from "./skillIcons"; // Importing icons

export default function Skills() {
  const [selectedCategory, setSelectedCategory] = useState(Object.keys(skills)[0]);

  // Memoized skill icons for better performance
  const memoizedIcons = useMemo(() => skillIcons, []);

  // Define experience levels dynamically
  const experienceLevels = {
    JavaScript: "游댮 Mastery",
    Python: "游댮 Mastery",
    Java: "游댯 Proficient",
    "C++": "游릭 Apprentice",
    HTML: "游댮 Mastery",
    CSS: "游댯 Proficient",
    Swift: "游릭 Apprentice",
    React: "游댯 Proficient",
    Django: "游댯 Proficient",
    "Unreal Engine": "游댯 Proficient",
    Unity: "游댯 Proficient",
    Blender: "游릭 Apprentice",
    AWS: "游댯 Proficient",
    MySQL: "游댯 Proficient",
    Postgres: "游댮 Mastery",
    "Node.js": "游댯 Proficient",
    Postman: "游릭 Apprentice",
    VR: "游댯 Proficient",
    Git: "游댮 Mastery",
    MongoDB: "游댯 Proficient",
  };

  return (
    <section id="skills" className="relative bg-gray-800 text-center">
      <div className="container px-5 py-10 mx-auto">
        {/* Section Title */}
        <div className="mb-10">
          <ChipIcon className="w-10 mx-auto mb-4 text-purple-100" />
          <h1 className="sm:text-4xl text-3xl font-medium title-font text-purple-100">
            Tech Stack & Skills
          </h1>
        </div>

        {/* Category Tabs */}
        <div className="flex justify-center mb-6 space-x-3">
          {Object.keys(skills).map((category) => (
            <button
              key={category}
              onClick={() => setSelectedCategory(category)}
              className={`px-4 py-2 font-medium rounded-lg transition-all duration-300 ${
                selectedCategory === category
                  ? "bg-purple-600 text-white"
                  : "bg-gray-600 text-gray-300 hover:bg-purple-500"
              }`}
            >
              {category}
            </button>
          ))}
        </div>

        {/* Skills Grid */}
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-6 justify-center items-center">
          {skills[selectedCategory].map((skill) => (
            <motion.div
              key={skill}
              className="flex flex-col items-center p-4 rounded-lg group bg-gray-700 hover:bg-gray-600 transition-all duration-300"
              whileHover={{ scale: 1.1 }}
            >
              {memoizedIcons[skill] || <span className="text-white">{skill}</span>}
              <span className="mt-2 text-white">{skill}</span>
              <span className="text-sm text-gray-400">
                {experienceLevels[skill] || "游릭 Apprentice"}
              </span>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
}
