import React, { useState, useRef, useEffect } from "react";
import Chatbot from "./chatbot.js";
import "./styles.css";

export default function About() {
  const [chatOpened, setChatOpened] = useState(false);
  const chatbotWrapperRef = useRef(null);

  // Listen for clicks on the Chatbot's buttons to toggle the introductory text.
  useEffect(() => {
    const container = chatbotWrapperRef.current;
    if (container) {
      const handleClick = (e) => {
        if (e.target.closest(".chat-button")) {
          setChatOpened(true);
        } else if (e.target.closest(".close-btn")) {
          setChatOpened(false);
        }
      };
      container.addEventListener("click", handleClick);
      return () => container.removeEventListener("click", handleClick);
    }
  }, []);

  return (
    <section id="about">
      {/*
        1) Parent: fixed height of 450px, flex container
           to align child columns side by side.
      */}
      <div
        className="mx-auto flex items-stretch"
        style={{ width: "100%", maxWidth: "1200px", height: "450px" }}
      >
        {/*
          2) Left column: flex-1 so it stretches to fill remaining space,
             and then center its content vertically/horizontally.
        */}
        <div className="flex-1 flex flex-col justify-center items-center text-center">
          {!chatOpened && (
            <p className="text-gray-700 text-lg leading-relaxed mb-4">
              Hello! My name is Annie. Have any questions?
            </p>
          )}
          <div ref={chatbotWrapperRef}>
            <Chatbot />
          </div>
        </div>

        {/*
          3) Right column: fixed width/height of 450px
             so it matches the parent's 450px height.
        */}
        <div
          className="glass-effect overflow-hidden rounded-lg shadow-lg"
          style={{ width: "450px", height: "450px" }}
        >
          <img
            src="/Headshot.JPG"
            alt="Headshot"
            className="object-cover w-full h-full"
          />
        </div>
      </div>
    </section>
  );
}
