import React from "react";
import Chatbot from "./chatbot.js"; // ✅ Import Chatbot component
import './styles.css';

export default function About() {
  return (
    <section id="about">
      <div className="container mx-auto flex px-10 py-20 md:flex-row flex-col items-center relative">

        {/* Left Side (Chatbot) */}
        <div className="relative">
          {/* Chatbot - Positioned to the Left of Image */}
          <div className="chatbot-container" style={{
            position: "absolute",
            right: "calc(100% + 20px)", // Moves chatbot to the left of image
            top: "50%",
            transform: "translateY(-50%)", // Centers it vertically
            zIndex: 1000,
          }}>
            <Chatbot />
          </div>
        </div>

        {/* Right Side (Image) */}
        <div>
          <div className="glass-effect w-full h-full overflow-hidden rounded-lg shadow-lg" style={{ width: '450px', height: '450px' }}>
            <img
              src="/Headshot.JPG"
              alt="Headshot"
              className="object-cover w-full h-full"
            />
          </div>
        </div>

      </div>
    </section>
  );
}
