import React from "react";
import Chatbot from "./chatbot.js"; // ✅ Import Chatbot component
import './styles.css';

export default function About() {
  return (
    <section id="about">
      <div className="container mx-auto flex px-10 py-20 md:flex-row flex-col items-center">

        {/* Left Side - Chatbot */}
        <div className="flex items-center">
          <div className="chatbot-container" style={{
            position: "relative", // No absolute positioning to keep spacing
            marginRight: "40px", // Adds spacing between chatbot and image
          }}>
            <Chatbot />
          </div>
        </div>

        {/* Right Side - Image */}
        <div>
          <div className="glass-effect w-full h-full overflow-hidden rounded-lg shadow-lg"
            style={{ width: '450px', height: '450px' }}>
            <img
              src="/Headshot.JPG"
              alt="Headshot"
              className="object-cover w-full h-full"
            />
          </div>
        </div>

      </div>
    </section>
  );
}
