import React from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import icons

const experienceLevels = {
  JavaScript: 90,
  Python: 95,
  Java: 80,
  "C++": 60,
  HTML: 90,
  CSS: 85,
  Swift: 55,
  React: 85,
  Django: 80,
  "Unreal Engine": 75,
  Unity: 75,
  Blender: 50,
  AWS: 80,
  MySQL: 80,
  Postgres: 95,
  "Node.js": 85,
  Postman: 60,
  VR: 75,
  Git: 95,
  MongoDB: 80,
};

export default function SkillItem({ skill }) {
  const progress = experienceLevels[skill] || 50; // Default to 50% if not listed

  // React Spring animation for progress bar
  const meterSpring = useSpring({
    width: `${progress}%`,
    from: { width: "0%" },
    config: { tension: 200, friction: 20 },
  });

  return (
    <div className="group flex flex-col items-center">
      {/* Circular Skill Icon */}
      <div className="relative w-20 h-20 flex items-center justify-center rounded-full bg-gray-700 group-hover:bg-gray-600 transition-all duration-300">
        {skillIcons[skill] || <span className="text-white">{skill}</span>}
      </div>

      {/* Skill Name */}
      <span className="mt-2 text-white">{skill}</span>

      {/* Progress Meter */}
      <div className="w-20 h-2 bg-gray-600 rounded-full mt-2 overflow-hidden">
        <animated.div
          className="h-2 bg-green-500 rounded-full"
          style={meterSpring}
        />
      </div>
    </div>
  );
}
