import React from "react";
import Chatbot from "chatbot.js"; // ✅ Import Chatbot component
import 'styles.css';

export default function About() {
  return (
    <section id="about">
      <div className="container mx-auto flex px-10 py-20 md:flex-row flex-col items-center">

        {/* ✅ Replace Text with Chatbot */}
        <div className="lg:flex-grow md:w-1/2 lg:pr-24 md:pr-16 flex flex-col md:items-start md:text-left mb-16 md:mb-0 items-center text-center">
          <h1 className="text-4xl font-bold text-gray-800 mb-4">Chat with Me!</h1>
          <p className="text-gray-700 text-lg leading-relaxed mb-4">
            Ask me anything about my experience, projects, or interests.
          </p>

          {/* ✅ Chatbot Component */}
          <div className="chatbot-container">
            <Chatbot />
          </div>
        </div>

        {/* ✅ Keep Headshot on the Right */}
        <div className="lg:max-w-lg lg:w-full md:w-1/2 w-5/6">
          <div className="glass-effect w-full h-full overflow-hidden rounded-lg shadow-lg" style={{ width: '400px', height: '400px' }}>
            <img
              src="/Headshot.JPG"
              alt="Headshot"
              className="object-cover w-full h-full"
            />
          </div>
        </div>

      </div>
    </section>
  );
}
