import { BadgeCheckIcon, ChipIcon } from "@heroicons/react/solid";
import React, { useState} from "react";
import { skills } from "../data";
import './skills.css';


const categories = {
  Languages: ["JavaScript", "Python", "Java"],
  Frameworks: ["React", "Django", "Unreal Engine"],
  Tools: ["Postman", "Node.js", "Git"],
};


export default function Skills() {
  const [selectedCategory, setSelectedCategory] = useState("Languages");

  return (
    <section id="skills">
      <div className="container px-5 py-10 mx-auto">
        <div className="text-center mb-20">
          <ChipIcon className="w-10 inline-block mb-4" />
          <h1 className="sm:text-4xl text-3xl font-medium title-font text-gray-800 mb-4">
            Tech Stack and Skills
          </h1>
        </div>

        {/* Tabs for categories */}
        <div className="tabs flex justify-center mb-8">
          {Object.keys(categories).map((category) => (
            <button
              key={category}
              onClick={() => setSelectedCategory(category)}
              className={`px-4 py-2 mx-2 font-medium ${
                selectedCategory === category
                  ? "bg-purple-500 text-white"
                  : "bg-gray-300 text-gray-800"
              } rounded-lg hover:bg-purple-400`}
            >
              {category}
            </button>
          ))}
        </div>

        {/* Display skills for selected category */}
        <div className="tab-content flex flex-wrap lg:w-4/5 sm:mx-auto sm:mb-2 -mx-2">
          {categories[selectedCategory].map((skills) => (
            <div key={skills} className="p-2 sm:w-1/2 w-full">
              <div className="skill-item bg-gray-800 rounded flex p-4 h-full items-center">
                <BadgeCheckIcon className="text-purple-100 w-6 h-6 flex-shrink-0 mr-4" />
                <span className="title-font font-medium text-white">
                  {skills}
                </span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
