import React, { useState } from "react";
import Chatbot from "./chatbot.js";
import './styles.css';

export default function About() {
  const [showChatbot, setShowChatbot] = useState(false);

  return (
    <section id="about">
      <div className="container mx-auto flex px-10 py-20 md:flex-row flex-col items-center">
        {/* Left Side (Text initially, then Chatbot) */}
        <div className="lg:flex-grow md:w-1/2 lg:pr-24 md:pr-16 flex flex-col items-center text-center mb-16 md:mb-0">
          {showChatbot ? (
            <Chatbot />
          ) : (
            <>
              <p className="text-gray-700 text-lg leading-relaxed mb-4">
                Hello! My name is Annie. Have any questions?
              </p>
              <button 
                onClick={() => setShowChatbot(true)}
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
              >
                Chat with me
              </button>
            </>
          )}
        </div>

        {/* Right Side (Image) */}
        <div
          className="glass-effect w-full h-full overflow-hidden rounded-lg shadow-lg"
          style={{ width: "450px", height: "450px" }}
        >
          <img
            src="/Headshot.JPG"
            alt="Headshot"
            className="object-cover w-full h-full"
          />
        </div>
      </div>
    </section>
  );
}
