import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

// Skill level mappings
const levelToPercentage = {
  Apprentice: 50,
  Proficient: 75,
  Mastery: 100,
};

// Skill level colors
const levelToColor = {
  Apprentice: "#F44336", // Red
  Proficient: "#FFC107", // Yellow
  Mastery: "#4CAF50", // Green
};

export default function SkillItem({ skill }) {
  const { name, level } = skill || {}; // ✅ Ensure skill is always defined
  const progress = levelToPercentage[level] || 50; // Default 50%
  const [isHovered, setIsHovered] = useState(false); // ✅ Hook always at top level

  // Animated effects for scaling icon
  const iconSpring = useSpring({
    transform: isHovered ? "scale(1.15)" : "scale(1)",
    config: { tension: 200, friction: 15 },
  });

  // Animated circular progress ring
  const circleSpring = useSpring({
    strokeDashoffset: isHovered ? 100 - progress : 100,
    opacity: isHovered ? 1 : 0,
    config: { tension: 200, friction: 20 },
  });

  // Circle SVG properties
  const radius = 30;
  const circumference = 2 * Math.PI * radius;

  return (
    <div
      className="group flex flex-col items-center relative"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Only render if skill data is valid */}
      {name && level ? (
        <>
          {/* SVG Circular Progress Ring (appears on hover) */}
          <svg className="absolute w-28 h-28" viewBox="0 0 80 80">
            <circle cx="40" cy="40" r={radius} stroke="#333" strokeWidth="6" fill="none" />
            <animated.circle
              cx="40"
              cy="40"
              r={radius}
              stroke={levelToColor[level] || "#F44336"}
              strokeWidth="6"
              fill="none"
              strokeDasharray={circumference}
              strokeDashoffset={circleSpring.strokeDashoffset.interpolate(
                (offset) => (offset / 100) * circumference
              )}
              strokeLinecap="round"
              className="transition-all duration-500"
            />
          </svg>

          {/* Circular Skill Icon */}
          <div
            className={`relative w-24 h-24 flex items-center justify-center rounded-full bg-gray-700 transition-all duration-300 ${
              isHovered ? "shadow-lg" : ""
            }`}
          >
            <animated.div style={iconSpring}>
              {skillIcons[name] || <span className="text-white">{name}</span>}
            </animated.div>
          </div>

          {/* Skill Name */}
          <span className="mt-2 text-white">{name}</span>

          {/* Experience Level Label */}
          <span
            className={`text-sm font-medium mt-2 ${
              level === "Mastery" ? "text-green-400" : level === "Proficient" ? "text-yellow-400" : "text-red-400"
            }`}
          >
            {level}
          </span>
        </>
      ) : (
        <p className="text-gray-400">Invalid skill data</p>
      )}
    </div>
  );
}
