import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

const levelToPercentage = {
  Beginner: 40,
  Proficient: 75,
  Mastery: 100,
};

const levelToColor = {
  Beginner: "#F44336", // Red
  Proficient: "#FFC107", // Yellow
  Mastery: "#4CAF50", // Green
};

export default function SkillItem({ skillName, skillLevel }) {
  const [isHovered, setIsHovered] = useState(false);

  // Progress bar animation
  const progressSpring = useSpring({
    width: isHovered ? levelToPercentage[skillLevel] : 0,
    config: { tension: 200, friction: 15 },
  });

  return (
    <div
      className="flex flex-col items-center justify-center w-28 h-28 rounded-full bg-gray-800 transition-transform duration-300 hover:scale-105 relative"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Skill Icon (Circular) */}
      <div className="flex items-center justify-center w-15 h-15 ">
        {skillIcons[skillName] || <span className="text-white">{skillName}</span>}
      </div>

      {/* Skill Name */}
      <span className="text-white text-sm font-semibold mt-2">{skillName}</span>

      {/* Progress Bar (Only on Hover) */}
      {isHovered && (
        <div className="absolute bottom-[-10px] w-full h-2 bg-gray-600 rounded-md overflow-hidden">
          <animated.div
            className="h-full rounded-md"
            style={{
              backgroundColor: levelToColor[skillLevel],
              width: progressSpring.width.to((w) => `${w}%`),
            }}
          />
        </div>
      )}
    </div>
  );
}
