import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

const levelToPercentage = {
  Beginner: 40,
  Proficient: 75,
  Mastery: 100,
};

const levelToColor = {
  Beginner: "#F44336", // Red
  Proficient: "#FFC107", // Yellow
  Mastery: "#4CAF50", // Green
};

export default function SkillItem({ skill }) {
  const { name, level } = skill || {};
  const [isHovered, setIsHovered] = useState(false);

  // Icon scaling animation
  const iconSpring = useSpring({
    transform: isHovered ? "scale(1.3)" : "scale(1)",
    config: { tension: 200, friction: 12 },
  });

  // Progress bar animation (appears when hovered)
  const progressSpring = useSpring({
    width: isHovered ? `${levelToPercentage[level]}%` : "0%",
    config: { tension: 200, friction: 15 },
  });

  return (
    <div
      className="relative flex flex-col items-center justify-center w-24 h-24"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Enlarging Circular Skill Icon */}
      <animated.div
        style={iconSpring}
        className="w-20 h-20 flex items-center justify-center rounded-full bg-gray-700 transition-all duration-300"
      >
        {skillIcons[name] || <span className="text-white">{name}</span>}
      </animated.div>

      {/* Skill Level Bar (Appears Below on Hover) */}
      <div className="relative w-32 h-6 mt-3 rounded-full bg-gray-800 overflow-hidden transition-all duration-300">
        <animated.div
          style={progressSpring}
          className="absolute left-0 top-0 h-full bg-opacity-80 rounded-full"
          style={{
            backgroundColor: levelToColor[level] || "#F44336",
          }}
        ></animated.div>

        {/* Skill Name & Level (Inside Progress Bar) */}
        {isHovered && (
          <span className="absolute inset-0 flex justify-center items-center text-white text-sm font-medium">
            {name} - {level}
          </span>
        )}
      </div>
    </div>
  );
}
