import React, { useState, useRef, useEffect } from "react";
import Chatbot from "./chatbot.js";
import './styles.css';

export default function About() {
  const [chatOpened, setChatOpened] = useState(false);
  const chatbotWrapperRef = useRef(null);

  useEffect(() => {
    const container = chatbotWrapperRef.current;
    if (container) {
      const handleClick = (e) => {
        if (e.target.closest('.chat-button')) {
          setChatOpened(true);
        } else if (e.target.closest('.close-btn')) {
          setChatOpened(false);
        }
      };
      container.addEventListener('click', handleClick);
      return () => container.removeEventListener('click', handleClick);
    }
  }, []);

  return (
    <section id="about" className="py-10 px-5 md:px-10">
  <div className="container mx-auto flex flex-col md:flex-row items-center gap-8">
    {/* Chat with me section */}
    <div className="w-full md:w-1/2 relative">
      {/* This inner div is absolutely positioned only on md+ screens */}
      <div className="md:absolute md:top-1/2 md:left-0 md:right-0 md:transform md:-translate-y-1/2 text-center">
        {!chatOpened && (
          <p className="text-gray-700 text-lg md:text-xl font-semibold leading-relaxed mb-4">
            Hello! My name is Annie. Have any questions?
          </p>
        )}
        <div ref={chatbotWrapperRef} className="w-full max-w-md mx-auto">
          <Chatbot />
        </div>
      </div>
    </div>

    {/* Image Section */}
    <div className="glass-effect w-full md:w-1/2 max-w-xs md:max-w-sm lg:max-w-md aspect-square overflow-hidden rounded-xl shadow-lg">
      <img
        src="/Headshot.JPG"
        alt="Headshot"
        className="object-cover w-full h-full"
      />
    </div>
  </div>
</section>



  );
}
