import React, { useState } from "react";
import { BadgeCheckIcon, ChipIcon } from "@heroicons/react/solid";
import { skills } from "../data"; // Import your skills data
import './skills.css';
import { SiPython, SiCplusplus, SiReact, SiDjango, SiUnrealengine, SiUnity, SiPostman, SiGit, SiBlender, SiMysql, SiPostgresql } from "react-icons/si"; // Import icons
import { IoLogoJavascript } from "react-icons/io5";
import { FaNodeJs, FaJava, FaSwift, FaHtml5, FaCss3Alt, FaVrCardboard,   } from "react-icons/fa";

// Map the skills to their corresponding icons
const skillIcons = {
  JavaScript: <IoLogoJavascript className="text-yellow-500 w-10 h-10" />,
  Python: <SiPython className="text-blue-500 w-10 h-10" />,
  Java: <FaJava className="text-red-600 w-10 h-10" />,
  "C++": <SiCplusplus className="text-blue-700 w-10 h-10" />,
  Swift: <FaSwift className="text-blue-500 w-10 h-10" />,
  HTML: <FaHtml5 className="text-blue-500 w-10 h-10" />,
  CSS: <FaCss3Alt className="text-blue-500 w-10 h-10" />,
  React: <SiReact className="text-blue-400 w-10 h-10" />,
  Django: <SiDjango className="text-green-500 w-10 h-10" />,
  Blender: <SiBlender className="text-green-500 w-10 h-10" />,
  "Unreal Engine": <SiUnrealengine className="text-gray-500 w-10 h-10" />,
  Unity: <SiUnity className="text-black w-10 h-10" />,
  Postman: <SiPostman className="text-orange-500 w-10 h-10" />,
  MySQL: <SiMysql className="text-orange-500 w-10 h-10" />,
  Postgres: <SiPostgresql className="text-orange-500 w-10 h-10" />,
  "Node.js": <FaNodeJs className="text-green-400 w-10 h-10" />,
  Git: <SiGit className="text-red-600 w-10 h-10" />,
  VR: <FaVrCardboard className="text-red-600 w-10 h-10" />,
};

export default function Skills() {
  const [selectedCategory, setSelectedCategory] = useState("Languages");

  return (
    <section id="skills" className="relative bg-gray-800">
      <div className="container px-5 py-10 mx-auto">
        <div className="text-center mb-20">
          <ChipIcon className="w-10 inline-block mb-4" />
          <h1 className="sm:text-4xl text-3xl font-medium title-font text-purple-100 mb-4">
            Tech Stack and Skills
          </h1>
        </div>

        {/* Tabs for categories */}
        <div className="tabs flex justify-center mb-8">
          {Object.keys(skills).map((category) => (
            <button
              key={category}
              onClick={() => setSelectedCategory(category)}
              className={`px-4 py-2 mx-2 font-medium ${
                selectedCategory === category
                  ? "bg-purple-100 text-gray-800"
                  : "bg-gray-300 text-gray-800"
              } rounded-lg hover:bg-purple-400`}
            >
              {category}
            </button>
          ))}
        </div>

        {/* Display skills for selected category */}
        <div className="skill-container">
          {skills[selectedCategory].map((skill) => (
            <div key={skill} className="skill-item">

                {skillIcons[skill]} {/* Display the skill icon */}
                <span className="title-font font-medium text-white absolute opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                  {skill} {/* Show the skill name on hover */}
                </span>
              </div>

          ))}
        </div>
      </div>
    </section>
  );
}
