import React, { useState } from "react";
import { ChipIcon } from "@heroicons/react/solid";
import { skills } from "../data"; // Import skills data
import SkillItem from "./skillItem"; // Import SkillItem component

export default function Skills() {
  const categories = Object.keys(skills);
  const [selectedCategory, setSelectedCategory] = useState(categories[0]); // Ensure valid category selection

  return (
    <section id="skills" className="relative bg-gray-800 text-center">
      <div className="container px-5 py-10 mx-auto">
        {/* Section Title */}
        <div className="mb-10">
          <ChipIcon className="w-10 mx-auto mb-4 text-purple-100" />
          <h1 className="sm:text-4xl text-3xl font-medium title-font text-purple-100">
            Tech Stack & Skills
          </h1>
        </div>

        {/* Category Tabs */}
        <div className="flex justify-center mb-6 space-x-3">
          {categories.map((category) => (
            <button
              key={category}
              onClick={() => setSelectedCategory(category)}
              className={`px-4 py-2 font-medium rounded-lg transition-all duration-300 ${
                selectedCategory === category
                  ? "bg-purple-600 text-white"
                  : "bg-gray-600 text-gray-300 hover:bg-purple-500"
              }`}
            >
              {category}
            </button>
          ))}
        </div>

        {/* Check if selectedCategory exists in skills */}
        {skills[selectedCategory] && skills[selectedCategory].length > 0 ? (
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-6 justify-center items-center">
            {skills[selectedCategory].map((skill, index) => (
              <SkillItem key={`${selectedCategory}-${index}`} skill={skill} />
            ))}
          </div>
        ) : (
          <p className="text-gray-400 mt-4">No skills available in this category.</p>
        )}
      </div>
    </section>
  );
}
