import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

const levelToPercentage = {
  Beginner: 40,
  Proficient: 75,
  Mastery: 100,
};

const levelToColor = {
  Beginner: "#F44336", // Red
  Proficient: "#FFC107", // Yellow
  Mastery: "#4CAF50", // Green
};

export default function SkillItem({ skill }) {
  const { name, level } = skill || {};
  const [isHovered, setIsHovered] = useState(false);

  // Animation for progress bar stroke (only triggers on hover)
  const progressSpring = useSpring({
    strokeDashoffset: isHovered ? 251 - (251 * levelToPercentage[level]) / 100 : 251, // Full circle initially
    config: { tension: 200, friction: 15 },
  });

  // Animation for icon scaling
  const iconSpring = useSpring({
    transform: isHovered ? "scale(1.2)" : "scale(1)",
    config: { tension: 200, friction: 15 },
  });

  return (
    <div
      className="group flex flex-col items-center justify-center w-28 h-28 relative overflow-visible"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Circular Progress Ring (Only Fills on Hover) */}
      <div className="absolute w-24 h-24">
        <svg className="absolute w-full h-full" viewBox="0 0 100 100">
          <circle
            cx="50"
            cy="50"
            r="40"
            stroke="#333"
            strokeWidth="6"
            fill="none"
          />
          <animated.circle
            cx="50"
            cy="50"
            r="40"
            stroke={levelToColor[level] || "#F44336"}
            strokeWidth="6"
            fill="none"
            strokeLinecap="round"
            strokeDasharray="251"
            strokeDashoffset={progressSpring.strokeDashoffset}
            className="transition-all duration-500"
          />
        </svg>
      </div>

      {/* Circular Skill Icon with Glow Effect (Only on Hover) */}
      <div
        className={`relative w-24 h-24 flex items-center justify-center rounded-full bg-gray-700 transition-all duration-300 aspect-square`}
        style={{
          filter: isHovered ? `drop-shadow(0 0 12px ${levelToColor[level]})` : "none",
        }}
      >
        <animated.div style={iconSpring}>
          {skillIcons[name] || <span className="text-white">{name}</span>}
        </animated.div>
      </div>

      {/* Skill Name & Expertise Level (Only Shows on Hover) */}
      {isHovered && (
        <div className="absolute bottom-[-30px] flex flex-col items-center">
          <span className="text-white text-sm font-semibold">{name}</span>
          <span
            className={`text-xs font-medium mt-1 ${
              level === "Mastery"
                ? "text-green-400"
                : level === "Proficient"
                ? "text-yellow-400"
                : "text-red-400"
            }`}
          >
            {level}
          </span>
        </div>
      )}
    </div>
  );
}
