import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

// Function to smoothly transition from red (low skill) to green (high skill)
const getSkillColor = (level) => {
  const red = Math.max(255 - level * 2.55, 0);
  const green = Math.min(level * 2.55, 255);
  return `rgb(${red}, ${green}, 80)`;
};

export default function SkillItem({ skillName, skillLevel }) {
  const [isHovered, setIsHovered] = useState(false);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });

  // Animation for progress bar reveal
  const progressSpring = useSpring({
    width: isHovered ? `${skillLevel}%` : "0%",
    opacity: isHovered ? 1 : 0,
    config: { tension: 200, friction: 20 },
  });

  // Animation for skill level text fade-in
  const textSpring = useSpring({
    opacity: isHovered ? 1 : 0,
    transform: isHovered ? "translateY(0px)" : "translateY(10px)",
    config: { tension: 150, friction: 20 },
  });

  return (
    <div
      className="relative flex flex-col items-center justify-center w-20 h-20"
      onMouseEnter={() => setIsHovered(true)}
      onMouseMove={(e) => setMousePos({ x: e.clientX, y: e.clientY })}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Skill Icon (Only Visible Initially) */}
      <div className="w-16 h-16 sm:w-20 sm:h-20 flex items-center justify-center rounded-full bg-gray-700 shadow-lg">
        {skillIcons[skillName] || <span className="text-white">{skillName}</span>}
      </div>

      {/* Subtle Progress Bar (Appears on Hover) */}
      <animated.div
        className="absolute bottom-[-8px] h-1 bg-gray-500 rounded-md"
        style={{
          width: progressSpring.width,
          opacity: progressSpring.opacity,
          backgroundColor: getSkillColor(skillLevel),
        }}
      />

      {/* Skill Level Text (Appears on Hover) */}
      <animated.span
        className="absolute bottom-[-22px] text-white text-xs font-semibold"
        style={textSpring}
      >
        {skillLevel}%
      </animated.span>

      {/* Skill Name Tooltip (Follows Cursor) */}
      {isHovered && (
        <div
          className="fixed bg-gray-800 text-white text-sm font-semibold px-3 py-1 rounded-lg shadow-lg pointer-events-none"
          style={{
            top: `${mousePos.y + 10}px`,
            left: `${mousePos.x + 15}px`,
            whiteSpace: "nowrap",
          }}
        >
          {skillName}
        </div>
      )}
    </div>
  );
}
