import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import { useDrag } from "react-use-gesture";
import skillIcons from "./skillIcons"; // Ensure skillIcons maps skill names to icons
import { skills } from "../data"; // Import categorized skills

export default function SkillItem({ category, skillsList }) {
  const [rotation, setRotation] = useState(0);

  // Handle drag rotation
  const bind = useDrag(({ movement: [mx] }) => {
    setRotation((prev) => prev + mx * 0.5);
  });

  return (
    <div className="relative w-full flex items-center justify-center mb-8">
      {/* Category Label on the Left */}
      <div className="absolute left-0 text-lg font-semibold text-white bg-gray-700 px-4 py-2 rounded-lg shadow-md">
        {category}
      </div>

      {/* Rotating Skill Icons */}
      <div {...bind()} className="relative w-[300px] h-[100px] flex items-center justify-center">
        {skillsList.map((skill, index) => {
          const angle = (360 / skillsList.length) * index;
          return (
            <animated.div
              key={skill.name}
              className="absolute w-14 h-14 flex items-center justify-center bg-gray-800 rounded-full shadow-lg"
              style={{
                transform: `rotate(${rotation + angle}deg) translate(120px) rotate(-${rotation + angle}deg)`,
              }}
            >
              {skillIcons[skill.name] || <span className="text-white text-xs">{skill.name}</span>}
            </animated.div>
          );
        })}
      </div>
    </div>
  );
}
