import React, { useState } from "react";
import "./chatbot.css"

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [streamingResponse, setStreamingResponse] = useState("");

  const sendMessage = async () => {
    if (!input.trim()) return;

    // ✅ Add user message to chat history
    const userMessage = { role: "user", content: input };
    setMessages((prev) => [...prev, userMessage]);
    setStreamingResponse(""); // ✅ Reset streaming text

    try {
      const eventSource = new EventSource(`http://localhost:8000/chat?message=${encodeURIComponent(input)}`);

      eventSource.onmessage = (event) => {
        setStreamingResponse((prev) => prev + event.data); // ✅ Append each chunk
      };

      eventSource.onerror = (error) => {
        console.error("Error streaming chat response:", error);
        eventSource.close();
      };

      eventSource.onclose = () => {
        setMessages((prev) => [...prev, { role: "bot", content: streamingResponse }]); // ✅ Finalize response
      };

    } catch (error) {
      console.error("Error communicating with chatbot:", error);
    }

    setInput(""); // ✅ Clear input field
  };

  const clearChat = () => {
    setMessages([]);
  }

  return (
    <div className="chat-container">
      <div className="chatbox">
        {messages.map((msg, index) => (
          <div key={index} className={msg.role === "user" ? "user-message" : "bot-message"}>
            {msg.content}
          </div>
        ))}
        {streamingResponse && (
          <div className="bot-message">
            {streamingResponse} {/* ✅ Show streaming response in real-time */}
          </div>
        )}
      </div>

      {/* ✅ Chat Input & Buttons */}
      <div className="chat-input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask me anything..."
        />
        <button onClick={sendMessage}>Send</button>
        <button className="clear-button" onClick={clearChat}>Clear</button>
      </div>
    </div>
  );
};

export default Chatbot;
