import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

// Skill level mappings
const levelToGlow = {
  Beginner: 50,
  Proficient: 75,
  Master: 100,
};

// Skill level colors
const levelToColor = {
  Apprentice: "#F44336", // Red
  Proficient: "#FFC107", // Yellow
  Mastery: "#4CAF50", // Green
};

export default function SkillItem({ skill }) {
  const { name, level } = skill || {}; // Ensure skill is always defined
  const [isHovered, setIsHovered] = useState(false);

  // Animated effects for scaling icon
  const iconSpring = useSpring({
    transform: isHovered ? "scale(1.15)" : "scale(1)",
    config: { tension: 200, friction: 15 },
  });


  return (
    <div
      className={`group flex flex-col items-center justify-center w-28 h-28 rounded-full bg-gray-800 p-4 transition-all duration-300
        ${isHovered ? `shadow-2xl ${levelToGlow[level]} drop-shadow-lg` : "shadow-md"}`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Circular Skill Icon with Glow */}
      <div
        className={`relative w-24 h-24 flex items-center justify-center rounded-full bg-gray-700 transition-all duration-300 ${
          isHovered ? "shadow-lg" : ""
        }`}
        style={{
          boxShadow: isHovered ? `0 0 15px ${levelToColor[level]}` : "none",
        }}
      >
        <animated.div style={iconSpring}>
          {skillIcons[name] || <span className="text-white">{name}</span>}
        </animated.div>
      </div>

      {/* Skill Name (Visible on Hover) */}
      <animated.span
        style={{ opacity: isHovered ? 1 : 0 }}
        className="mt-2 text-white text-sm font-semibold transition-opacity duration-300"
      >
        {name}
      </animated.span>

      {/* Experience Level Label */}
      <animated.span
        style={{ opacity: isHovered ? 1 : 0 }}
        className={`text-xs font-medium mt-2 transition-opacity duration-300 ${
          level === "Mastery"
            ? "text-green-400"
            : level === "Proficient"
            ? "text-yellow-400"
            : "text-red-400"
        }`}
      >
        {level}
      </animated.span>
    </div>
  );
}
