import React, { useState } from "react";
import "./chatbot.css"; // ✅ Import styles

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [streamingResponse, setStreamingResponse] = useState("");

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { role: "user", content: input };
    setMessages((prev) => [...prev, userMessage]);
    setStreamingResponse("");

    try {
      const eventSource = new EventSource(`http://localhost:8000/chat?message=${encodeURIComponent(input)}`);

      eventSource.onmessage = (event) => {
        setStreamingResponse((prev) => prev + event.data);
      };

      eventSource.onerror = () => {
        eventSource.close();
      };

      eventSource.onclose = () => {
        setMessages((prev) => [...prev, { role: "bot", content: streamingResponse }]);
      };

    } catch (error) {
      console.error("Error:", error);
    }

    setInput("");
  };

  return (
    <div className="chat-container">
      <h2 className="text-2xl font-bold text-gray-800 mb-4 text-center">Ask me anything!</h2>
      <div className="chatbox">
        {messages.map((msg, index) => (
          <div key={index} className={msg.role === "user" ? "user-message" : "bot-message"}>
            {msg.content}
          </div>
        ))}
        {streamingResponse && <div className="bot-message">{streamingResponse}</div>}
      </div>

      <div className="chat-input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask me anything..."
        />
        <button className="send" onClick={sendMessage}>Send</button>
        <button className="clear" onClick={() => setMessages([])}>Clear</button>
      </div>
    </div>
  );
};

export default Chatbot;
