import React, { useState, useEffect, useRef } from "react";
import "./chatbot.css";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [streamingResponse, setStreamingResponse] = useState("");
  const eventSourceRef = useRef(null); 

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { role: "user", content: input };
    setMessages((prev) => [...prev, userMessage]);
    setStreamingResponse("");


    try {
        const response = await fetch("http://localhost:8000/chat", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ message: input }),
        });

        const reader = response.body.getReader();
        const decoder = new TextDecoder("utf-8");
        let botMessage = { role: "bot", content: "" };

        // ✅ Read streaming response
        while (true) {
          const { value, done } = await reader.read();
          if (done) break;

          const chunk = decoder.decode(value, { stream: true });
          botMessage.content += chunk;

          setMessages((prev) => [...prev.slice(0, -1), botMessage]); // Update last message
        }

        setMessages((prev) => [...prev, botMessage]);
      } catch (error) {
        console.error("Error:", error);
      }

      setInput(""); // Clear input field
    };


  const clearMessages = () => {
    setMessages([]); 
    setStreamingResponse(""); 
    if (eventSourceRef.current) {
      eventSourceRef.current.close(); 
      eventSourceRef.current = null;
    }
  };

  return (
    <div className="chat-container">
      <h2 className="text-2xl font-bold text-gray-800 mb-4 text-center">Ask me anything!</h2>
      <div className="chatbox">
        {messages.map((msg, index) => (
          <div key={index} className={msg.role === "user" ? "user-message" : "bot-message"}>
            {msg.content}
          </div>
        ))}
        {streamingResponse && <div className="bot-message">{streamingResponse}</div>}
      </div>

      <div className="chat-input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask me anything..."
        />
        <button className="send" onClick={sendMessage}>Send</button>
        <button className="clear" onClick={clearMessages}>Clear</button>
      </div>
    </div>
  );
};

export default Chatbot;
