import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

// Define glow colors based on skill level
const levelToGlow = {
  Apprentice: "shadow-red-500", // Red Glow
  Proficient: "shadow-yellow-500", // Yellow Glow
  Mastery: "shadow-green-500", // Green Glow
};

export default function SkillItem({ skill }) {
  const { name, level } = skill || {}; // Ensure skill is always defined
  const [isHovered, setIsHovered] = useState(false);

  // Animated effects for scaling and glow
  const iconSpring = useSpring({
    transform: isHovered ? "scale(1.15)" : "scale(1)",
    config: { tension: 200, friction: 15 },
  });

  return (
    <div
      className={`group flex flex-col items-center justify-center w-28 h-28 rounded-lg bg-gray-800 p-4 transition-all duration-300 ${
        isHovered ? `shadow-xl ${levelToGlow[level]}` : "shadow-md"
      }`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Animated Icon */}
      <animated.div style={iconSpring}>
        {skillIcons[name] || <span className="text-white">{name}</span>}
      </animated.div>

      {/* Skill Name */}
      <span className="mt-2 text-white">{name}</span>
    </div>
  );
}
