import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import { useDrag } from "react-use-gesture";
import skillIcons from "./skillIcons"; // Ensure skillIcons maps skill names to icons
import { skills } from "../data"; // Import categorized skills

export default function SkillItem({ selectedCategory }) {
  const [rotation, setRotation] = useState(0);

  // Handle drag rotation
  const bind = useDrag(({ movement: [mx] }) => {
    setRotation((prev) => prev + mx * 0.2);
  });

  const categorySkills = skills[selectedCategory] || [];

  return (
    <div className="flex flex-col items-center">
      {/* Category Title */}
      <h2 className="text-xl font-semibold text-white mb-4">{selectedCategory}</h2>

      {/* Circular Skill Wheel */}
      <div {...bind()} className="relative w-60 h-60 rounded-full border-2 border-gray-500 flex items-center justify-center">
        {categorySkills.map((skill, index) => {
          const angle = (360 / categorySkills.length) * index;
          return (
            <animated.div
              key={skill.name}
              className="absolute w-14 h-14 flex items-center justify-center bg-gray-800 rounded-full shadow-lg"
              style={{
                transform: `rotate(${rotation + angle}deg) translate(100px) rotate(-${rotation + angle}deg)`,
              }}
            >
              {skillIcons[skill.name] || <span className="text-white text-xs">{skill.name}</span>}
            </animated.div>
          );
        })}
      </div>
    </div>
  );
}
