import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

const experienceLevels = {
  JavaScript: 90,
  Python: 95,
  Java: 80,
  "C++": 60,
  HTML: 90,
  CSS: 85,
  Swift: 55,
  React: 85,
  Django: 80,
  "Unreal Engine": 75,
  Unity: 75,
  Blender: 50,
  AWS: 80,
  MySQL: 80,
  Postgres: 95,
  "Node.js": 85,
  Postman: 60,
  VR: 75,
  Git: 95,
  MongoDB: 80,
};

// Function to determine the color of the glow and progress ring
const getColor = (level) => {
  if (level >= 86) return "#4CAF50"; // Green for Advanced
  if (level >= 61) return "#FFC107"; // Yellow for Intermediate
  return "#F44336"; // Red for Beginner
};

export default function SkillItem({ skill }) {
  const progress = experienceLevels[skill] || 50; // Default to 50% if not listed
  const [isHovered, setIsHovered] = useState(false);

  // Animation for icon scaling
  const iconSpring = useSpring({
    transform: isHovered ? "scale(1.15)" : "scale(1)",
    config: { tension: 200, friction: 15 },
  });

  // Animation for circular progress ring
  const circleSpring = useSpring({
    strokeDashoffset: isHovered ? 100 - progress : 100, // Fills the ring
    opacity: isHovered ? 1 : 0, // Show only on hover
    config: { tension: 200, friction: 20 },
  });

  // Circle SVG properties
  const radius = 30;
  const circumference = 2 * Math.PI * radius;

  return (
    <div
      className="group flex flex-col items-center relative"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* SVG Progress Ring (appears on hover) */}
      <svg className="absolute w-28 h-28" viewBox="0 0 80 80">
        <circle
          cx="40"
          cy="40"
          r={radius}
          stroke="#333" // Background ring
          strokeWidth="6"
          fill="none"
        />
        <animated.circle
          cx="40"
          cy="40"
          r={radius}
          stroke={getColor(progress)}
          strokeWidth="6"
          fill="none"
          strokeDasharray={circumference}
          strokeDashoffset={circleSpring.strokeDashoffset.interpolate(
            (offset) => (offset / 100) * circumference
          )}
          strokeLinecap="round"
          className="transition-all duration-500"
        />
      </svg>

      {/* Circular Icon with Glow on Hover */}
      <div
        className={`relative w-24 h-24 flex items-center justify-center rounded-full bg-gray-700 transition-all duration-300 ${
          isHovered ? "shadow-lg" : ""
        }`}
      >
        <animated.div style={iconSpring}>
          {skillIcons[skill] || <span className="text-white">{skill}</span>}
        </animated.div>
      </div>

      {/* Skill Name */}
      <span className="mt-2 text-white">{skill}</span>
    </div>
  );
}
