import React, { useState, useRef, useEffect } from "react";
import Chatbot from "./chatbot.js";
import './styles.css';

export default function About() {
  const [chatOpened, setChatOpened] = useState(false);
  const chatbotWrapperRef = useRef(null);

  // Listen for clicks on the chatbot buttons to toggle the introductory text.
  useEffect(() => {
    const container = chatbotWrapperRef.current;
    if (container) {
      const handleClick = (e) => {
        if (e.target.closest('.chat-button')) {
          setChatOpened(true);
        } else if (e.target.closest('.close-btn')) {
          setChatOpened(false);
        }
      };
      container.addEventListener('click', handleClick);
      return () => container.removeEventListener('click', handleClick);
    }
  }, []);

  return (
    <section id="about" className="min-h-screen flex items-center justify-center bg-gray-100">
      {/* 
        The section is now full viewport height (min-h-screen),
        and we use flex + items-center + justify-center 
        to center its children both vertically & horizontally.
      */}
      <div className="container mx-auto flex flex-col md:flex-row items-center justify-center">
        {/* LEFT COLUMN */}
        <div className="md:w-1/2 flex justify-center items-center">
          {/* 
            This extra container holds the text and the Chatbot.
            It's also set to flex-col + center alignment, so 
            everything stacks and centers together.
          */}
          <div className="flex flex-col items-center text-center">
            {!chatOpened && (
              <p className="text-gray-700 text-lg leading-relaxed mb-4">
                Hello! My name is Annie. Have any questions?
              </p>
            )}
            <div ref={chatbotWrapperRef}>
              <Chatbot />
            </div>
          </div>
        </div>

        {/* RIGHT COLUMN */}
        <div className="md:w-1/2 flex justify-center items-center">
          <div
            className="glass-effect overflow-hidden rounded-lg shadow-lg"
            style={{ width: "450px", height: "450px" }}
          >
            <img
              src="/Headshot.JPG"
              alt="Headshot"
              className="object-cover w-full h-full"
            />
          </div>
        </div>
      </div>
    </section>
  );
}
