import React, { useState, useMemo } from "react";
import { FaGithub } from "react-icons/fa";
import ParallaxTilt from "react-parallax-tilt";
import { projects } from "../data";
import "./projects.css";

// Single card component
function ProjectCard({ project, index, flippedIndex, setFlippedIndex }) {
  // This card is flipped if its index matches flippedIndex
  const isFlipped = flippedIndex === index;

  const handleFlip = () => {
    // If already flipped, unflip. Otherwise, flip this card and unflip others
    setFlippedIndex(isFlipped ? -1 : index);
  };

  return (
    <ParallaxTilt
      glareEnable={true}
      glareMaxOpacity={0.2}
      tiltMaxAngleX={8}
      tiltMaxAngleY={8}
      transitionSpeed={400}
      scale={1.02}
      className="w-full h-[400px]" /* container for the tilt effect */
    >
      <div
        className="flip-card relative w-full h-full cursor-pointer"
        onClick={handleFlip}
      >
        {/* Inner wrapper for the flipping effect */}
        <div className={`flip-card-inner ${isFlipped ? "flipped" : ""}`}>

          {/* FRONT SIDE */}
          <div className="flip-card-front absolute w-full h-full p-6 bg-[#1f2937] rounded-lg shadow-lg flex flex-col justify-center items-center text-center">
            <h2 className="text-2xl font-bold text-purple-100">
              {project.title}
            </h2>
            {project.subtitle && (
              <p className="text-sm text-purple-300 mt-2">{project.subtitle}</p>
            )}
          </div>

          {/* BACK SIDE */}
          <div className="flip-card-back absolute w-full h-full p-6 bg-[#1f2937] rounded-lg shadow-lg flex flex-col justify-center items-center text-center text-purple-100 transform rotate-y-180">
            <div className="items-center relative z-10">
              <p className="text-sm mb-2">{project.description}</p>
              {project.functions && (
                <>
                  <h4 className="font-semibold mb-1">Main Functions</h4>
                  <ul className="list-disc list-inside text-sm mb-2 text-left">
                    {project.functions.map((func, idx) => (
                      <li key={idx}>{func}</li>
                    ))}
                  </ul>
                </>
              )}
              <div className="flex flex-wrap gap-2 mt-4">
                {project.technologies?.map((tech) => (
                  <span
                    key={tech.name}
                    className="bg-purple-200 text-gray-900 px-2 py-1 rounded text-xs font-medium"
                  >
                    {tech.name}
                  </span>
                ))}
              </div>
              <div className="mt-4 flex gap-4 justify-center">
                {project.github && (
                  <a
                    href={project.github}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-purple-200 hover:text-white text-md inline-flex items-center"
                    onClick={(e) => e.stopPropagation()} // prevent flip on link click
                  >
                    <FaGithub className="mr-1" /> Code
                  </a>
                )}
                {project.link && (
                  <a
                    href={project.link}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-purple-200 hover:text-white text-md"
                    onClick={(e) => e.stopPropagation()} // prevent flip on link click
                  >
                    Live
                  </a>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </ParallaxTilt>
  );
}

// Main Projects component
export default function Projects() {
  const [activeTab, setActiveTab] = useState("completed");
  const [flippedIndex, setFlippedIndex] = useState(-1); // track which card is flipped

  const handleTabChange = (tab) => {
    setActiveTab(tab);
    setFlippedIndex(-1); // unflip all when switching tabs
  };

  const completedProjects = projects.filter((p) => p.status === "completed");
  const schoolCodeExamples = projects.filter((p) => p.category === "School");

  // Shuffle if desired
  const shuffledCompletedProjects = useMemo(() => {
    const shuffled = [...completedProjects];
    for (let i = shuffled.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
    }
    return shuffled;
  }, [completedProjects]);

  return (
    <section id="projects" className="text-purple-100 bg-gray-900 body-font">
      <div className="container px-4 py-10 mx-auto text-left lg:px-20">

        {/* Tabs */}
        <div className="flex justify-center mb-10">
          <button
            className={`px-4 py-2 font-bold rounded-lg mr-2 ${
              activeTab === "completed"
                ? "bg-purple-200 text-gray-700"
                : "bg-gray-800 text-purple-100"
            }`}
            onClick={() => handleTabChange("completed")}
          >
            Completed Projects
          </button>
          <button
            className={`px-4 py-2 font-bold rounded-lg ${
              activeTab === "school"
                ? "bg-purple-200 text-gray-700"
                : "bg-gray-800 text-purple-100"
            }`}
            onClick={() => handleTabChange("school")}
          >
            School Projects
          </button>
        </div>

        {/* Completed Projects Grid */}
        {activeTab === "completed" && (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
            {shuffledCompletedProjects.map((project, index) => (
              <ProjectCard
                key={project.title}
                project={project}
                index={index}
                flippedIndex={flippedIndex}
                setFlippedIndex={setFlippedIndex}
              />
            ))}
          </div>
        )}

        {/* School Projects Grid */}
        {activeTab === "school" && (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 mt-10">
            {schoolCodeExamples.map((project, index) => (
              <ProjectCard
                key={project.title}
                project={project}
                index={index}
                flippedIndex={flippedIndex}
                setFlippedIndex={setFlippedIndex}
              />
            ))}
          </div>
        )}
      </div>
    </section>
  );
}
