import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

const levelToPercentage = {
  Beginner: 40,
  Proficient: 75,
  Mastery: 100,
};

const levelToColor = {
  Beginner: "#F44336", // Red
  Proficient: "#FFC107", // Yellow
  Mastery: "#4CAF50", // Green
};

export default function SkillItem({ skillName, skillLevel }) {
  const [isHovered, setIsHovered] = useState(false);

  // Progress bar animation
  const progressSpring = useSpring({
    width: isHovered ? levelToPercentage[skillLevel] : 0,
    config: { tension: 200, friction: 15 },
  });

  return (
    <div
      className="flex flex-col items-center justify-center w-24 h-24 sm:w-28 sm:h-28 rounded-full 
      bg-gray-800 shadow-lg transition-transform duration-300 hover:scale-110 hover:bg-gray-700 relative"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Skill Icon with Glow Effect */}
      <div
        className={`w-16 h-16 sm:w-20 sm:h-20 flex items-center justify-center rounded-full 
        bg-gray-700 transition-all duration-300 shadow-lg 
        ${isHovered ? "shadow-xl shadow-purple-400" : ""}`}
      >
        {skillIcons[skillName] || (
          <span className="text-white text-lg">{skillName}</span>
        )}
      </div>

      {/* Skill Name */}
      <span className="text-white text-sm font-semibold mt-2">{skillName}</span>

      {/* Progress Bar (Only on Hover) */}
      {isHovered && (
        <div className="absolute bottom-[-10px] w-3/4 h-1 bg-gray-600 rounded-md overflow-hidden">
          <animated.div
            className="h-full rounded-md"
            style={{
              backgroundColor: levelToColor[skillLevel],
              width: progressSpring.width.to((w) => `${w}%`),
            }}
          />
        </div>
      )}
    </div>
  );
}
