// Updated Projects.js to use Accordion/Expandable List View for Completed Projects
import { projects } from "../data";
import {
  FaGithub, FaChevronDown, FaChevronUp
} from "react-icons/fa";
import React, { useState, useMemo } from "react";
import "./projects.css";

export default function Projects() {
  const [activeTab, setActiveTab] = useState("completed");
  const [expandedProjectId, setExpandedProjectId] = useState(null);

  const handleTabChange = (tab) => {
    setActiveTab(tab);
    setExpandedProjectId(null);
  };

  const completedProjects = projects.filter(p => p.status === "completed");
  const schoolCodeExamples = projects.filter(p => p.category === "School");

  const shuffledCompletedProjects = useMemo(() => {
    const shuffled = [...completedProjects];
    for (let i = shuffled.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
    }
    return shuffled;
  }, [projects]);

  const toggleExpand = (title) => {
    setExpandedProjectId(prev => (prev === title ? null : title));
  };

  return (
    <section id="projects" className="text-gray-800 bg-purple-100 body-font">
      <div className="container px-4 py-10 mx-auto text-center lg:px-20">
        <div className="flex justify-center mb-8">
          <button
            className={`px-4 py-2 font-bold ${activeTab === "completed" ? "bg-gray-800 text-purple-100" : "bg-purple-100 text-gray-800"} rounded-lg mr-2`}
            onClick={() => handleTabChange("completed")}
          >
            Completed Projects
          </button>
          <button
            className={`px-4 py-2 font-bold ${activeTab === "school" ? "bg-gray-800 text-purple-100" : "bg-purple-100 text-gray-800"} rounded-lg`}
            onClick={() => handleTabChange("school")}
          >
            School Projects
          </button>
        </div>

        {activeTab === "completed" && (
          <div className="space-y-4">
            {shuffledCompletedProjects.map(project => (
              <div key={project.id || project.title} className="bg-gray-800 text-purple-100 p-6 rounded-lg shadow-md text-left">
                <div className="flex justify-between items-center cursor-pointer" onClick={() => toggleExpand(project.title)}>
                  <h3 className="text-xl font-semibold">{project.title}</h3>
                  {expandedProjectId === project.title ? <FaChevronUp /> : <FaChevronDown />}
                </div>
                {expandedProjectId === project.title && (
                  <div className="mt-4">
                    <p className="text-sm mb-2">{project.description}</p>
                    {project.functions && (
                      <>
                        <h4 className="font-semibold mb-1">Main Functions</h4>
                        <ul className="list-disc list-inside text-sm mb-2">
                          {project.functions.map((func, idx) => (
                            <li key={idx}>{func}</li>
                          ))}
                        </ul>
                      </>
                    )}
                    <div className="flex space-x-4 mt-4">
                      {project.link && (
                        <a
                          href={project.link}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="px-4 py-2 bg-purple-100 text-gray-900 rounded hover:bg-white"
                        >
                          Live
                        </a>
                      )}
                      {project.github && (
                        <a
                          href={project.github}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="px-4 py-2 bg-purple-100 text-gray-900 rounded hover:bg-white flex items-center space-x-2"
                        >
                          <FaGithub /> <span>Code</span>
                        </a>
                      )}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}

        {activeTab === "school" && (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {schoolCodeExamples.map((project) => (
              <div key={project.id} className="p-6 bg-gray-800 rounded-lg shadow-lg">
                <h2 className="text-lg font-bold text-purple-100 text-center mb-4">{project.title}</h2>
                <p className="text-sm text-purple-200">{project.description}</p>
                {project.github && (
                  <a href={project.github} target="_blank" rel="noopener noreferrer" className="block mt-4 text-purple-300 hover:text-white">
                    <FaGithub className="inline-block mr-1" /> View Code
                  </a>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </section>
  );
}
