import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

// Function to generate a smooth gradient from red (low skill) to green (high skill)
const getSkillColor = (level) => {
  const red = Math.max(255 - level * 2.55, 0); // Reduce red as skill increases
  const green = Math.min(level * 2.55, 255); // Increase green as skill increases
  return `rgb(${red}, ${green}, 80)`; // Smooth gradient red → yellow → green
};

export default function SkillItem({ skillName, skillLevel }) {
  const [isHovered, setIsHovered] = useState(false);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });

  // Fix: Ensure width is always a number inside `useSpring`
  const progressSpring = useSpring({
    width: isHovered ? skillLevel : 0, // Keep this as a number (0-100)
    config: { tension: 200, friction: 15 },
  });

  return (
    <div
      className="relative flex items-center justify-center"
      onMouseEnter={() => setIsHovered(true)}
      onMouseMove={(e) => setMousePos({ x: e.clientX, y: e.clientY })}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Skill Icon */}
      <div
        className="w-16 h-16 sm:w-20 sm:h-20 flex items-center justify-center rounded-full 
        bg-gray-700 shadow-lg transition-transform duration-300 hover:scale-110"
      >
        {skillIcons[skillName] || <span className="text-white">{skillName}</span>}
      </div>

      {/* Hover Name Tooltip (Properly Follows Cursor) */}
      {isHovered && (
        <div
          className="fixed bg-gray-800 text-white text-sm font-semibold px-3 py-1 rounded-lg shadow-lg pointer-events-none"
          style={{
            top: `${mousePos.y + 10}px`,
            left: `${mousePos.x + 15}px`,
            whiteSpace: "nowrap",
          }}
        >
          {skillName}
        </div>
      )}

      {/* Progress Bar (Fix: `width` remains a number and is converted in JSX) */}
      {isHovered && (
        <div className="absolute bottom-[-10px] w-3/4 h-1 bg-gray-600 rounded-md overflow-hidden">
          <animated.div
            className="h-full rounded-md"
            style={{
              backgroundColor: getSkillColor(skillLevel), // Apply color dynamically
              width: progressSpring.width.to((w) => `${w}%`), // Convert number to string only in JSX
            }}
          />
        </div>
      )}
    </div>
  );
}
