import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

// Function to determine skill level category
const getSkillCategory = (level) => {
  if (level <= 40) return "Beginner";
  if (level <= 75) return "Proficient";
  return "Master";
};

// Function to get arc count based on skill level
const getArcCount = (level) => {
  if (level <= 40) return 1; // Beginner: 1 arc
  if (level <= 75) return 2; // Proficient: 2 arcs
  return 3; // Master: 3 arcs
};

// Function to determine arc colors for each proficiency level
const getArcColor = (index) => {
  const colors = ["#F44336", "#FFC107", "#4CAF50"]; // Red → Yellow → Green
  return colors[index]; // Assign color based on arc index
};

export default function SkillItem({ skillName, skillLevel }) {
  const [isHovered, setIsHovered] = useState(false);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });

  const category = getSkillCategory(skillLevel);
  const arcCount = getArcCount(skillLevel);

  return (
    <div
      className="relative flex items-center justify-center"
      onMouseEnter={() => setIsHovered(true)}
      onMouseMove={(e) => setMousePos({ x: e.clientX, y: e.clientY })}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Arcs Appear on Hover */}
      {isHovered && (
        <svg className="absolute w-28 h-28" viewBox="0 0 100 100">
          {/* Render multiple arcs dynamically */}
          {Array.from({ length: arcCount }).map((_, index) => (
            <path
              key={index}
              d="M10,50 A40,40 0 0,1 90,50"
              stroke={getArcColor(index)}
              strokeWidth={4 + index * 2} // Each arc gets thicker
              fill="none"
              strokeLinecap="round"
            />
          ))}
        </svg>
      )}

      {/* Skill Icon */}
      <div className="w-16 h-16 sm:w-20 sm:h-20 flex items-center justify-center rounded-full bg-gray-700 shadow-lg">
        {skillIcons[skillName] || <span className="text-white">{skillName}</span>}
      </div>

      {/* Skill Name Tooltip (Follows Cursor) */}
      {isHovered && (
        <div
          className="fixed bg-gray-900 text-white text-sm font-semibold px-3 py-1 rounded-lg shadow-lg pointer-events-none"
          style={{
            top: `${mousePos.y + 10}px`,
            left: `${mousePos.x + 15}px`,
            whiteSpace: "nowrap",
          }}
        >
          {skillName} ({category})
        </div>
      )}
    </div>
  );
}
