import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

const levelToPercentage = {
  Beginner: 40,
  Proficient: 75,
  Mastery: 100,
};

const levelToColor = {
  Beginner: "#F44336", // Red
  Proficient: "#FFC107", // Yellow
  Mastery: "#4CAF50", // Green
};

export default function SkillItem({ skillName, skillLevel }) {
  const [isHovered, setIsHovered] = useState(false);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });

  // Progress bar animation (fix: always animates properly)
  const progressSpring = useSpring({
    width: isHovered ? levelToPercentage[skillLevel] : 0,
    config: { tension: 200, friction: 15 },
  });

  return (
    <div
      className="relative flex items-center justify-center"
      onMouseEnter={() => setIsHovered(true)}
      onMouseMove={(e) => setMousePos({ x: e.clientX, y: e.clientY })}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Skill Icon */}
      <div
        className="w-16 h-16 sm:w-20 sm:h-20 flex items-center justify-center rounded-full 
        bg-gray-700 shadow-lg transition-transform duration-300 hover:scale-110"
      >
        {skillIcons[skillName] || <span className="text-white">{skillName}</span>}
      </div>

      {/* Hover Name Tooltip (Fix: Properly Positioned) */}
      {isHovered && (
        <div
          className="absolute bg-gray-800 text-white text-sm font-semibold px-3 py-1 rounded-lg shadow-lg pointer-events-none"
          style={{
            top: `${mousePos.y - 50}px`, // Fix: properly position tooltip
            left: `${mousePos.x + 15}px`,
            whiteSpace: "nowrap",
          }}
        >
          {skillName}
        </div>
      )}

      {/* Progress Bar (Fix: Always Displays Properly) */}
      {isHovered && (
        <div className="absolute bottom-[-10px] w-3/4 h-1 bg-gray-600 rounded-md overflow-hidden">
          <animated.div
            className="h-full rounded-md"
            style={{
              backgroundColor: levelToColor[skillLevel] || "#F44336",
              width: progressSpring.width.to((w) => `${w}%`),
            }}
          />
        </div>
      )}
    </div>
  );
}
