import React, { useState } from "react";
import { skills } from "../data"; // Import categorized skills
import skillIcons from "./skillIcons"; // Map skill names to icons


// Convert skills data to a tree structure
export default function SkillWheel() {
    const categories = Object.keys(skills);
    const [selectedCategory, setSelectedCategory] = useState(categories[0]);
    const categorySkills = skills[selectedCategory];

    // Handle drag-based rotation
    const [rotation, setRotation] = useState(0);
    const bind = useDrag(({ movement: [mx] }) => {
      setRotation((prev) => prev + mx * 0.5);
    });

    return (
      <div className="flex justify-center items-center space-x-8">
        {/* Sidebar for Categories */}
        <div className="flex flex-col space-y-3">
          {categories.map((category) => (
            <button
              key={category}
              onClick={() => setSelectedCategory(category)}
              className={`px-4 py-2 text-white font-medium rounded-md transition duration-300 ${
                selectedCategory === category ? "bg-blue-500" : "bg-gray-700 hover:bg-gray-500"
              }`}
            >
              {category}
            </button>
          ))}
        </div>

        {/* Rotating Skill Icons */}
        <div {...bind()} className="relative w-60 h-60 rounded-full border-2 border-gray-500 flex items-center justify-center">
          {categorySkills.map((skill, index) => {
            const angle = (360 / categorySkills.length) * index;
            return (
              <motion.div
                key={skill.name}
                className="absolute w-12 h-12 flex items-center justify-center bg-gray-800 rounded-full shadow-lg"
                style={{
                  transform: `rotate(${rotation + angle}deg) translate(100px) rotate(-${rotation + angle}deg)`,
                }}
              >
                {skillIcons[skill.name] || <span className="text-white text-xs">{skill.name}</span>}
              </motion.div>
            );
          })}
        </div>
      </div>
    );
  }
