import React, { useState, useEffect, useRef } from "react";
import "./chatbot.css";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [isOpen, setIsOpen] = useState(false);
  const [streamingResponse, setStreamingResponse] = useState("");
  const eventSourceRef = useRef(null); 
  const chatContainerRef = useRef(null);

  const sendMessage = async () => {
    if (!input.trim()) return;
    // ... (your existing message-sending logic)
  };

  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [messages]);

  const clearMessages = () => {
    setMessages([]);
    setStreamingResponse("");
    setIsTyping(false);
    if (eventSourceRef.current) {
      eventSourceRef.current.close();
      eventSourceRef.current = null;
    }
  };

  return (
    <div
      className="chatbot-wrapper"
      style={{ width: "350px", position: "relative" }} // Ensure wrapper is positioned normally
    >
      {!isOpen && (
        <button
          className="chat-button"
          onClick={() => setIsOpen(true)}

        >
          ðŸ’¬ Chat with me
        </button>
      )}

      {isOpen && (
        <div
          className="chat-container show-chat">
          <div
            className="chat-header"

          >
            <h2>Chat with me</h2>
            <button className="close-btn" onClick={() => setIsOpen(false)}>
              âœ–
            </button>
          </div>

          <div
            className="chatbox"
            ref={chatContainerRef}
            style={{ flex: 1, overflowY: "auto", padding: "10px" }}
          >
            {messages.map((msg, index) => (
              <div
                key={index}
                className={msg.role === "user" ? "user-message" : "bot-message"}
              >
                {msg.content}
              </div>
            ))}
            {streamingResponse && (
              <div className="bot-message">{streamingResponse}</div>
            )}
            {isTyping && (
              <div className="bot-typing">
                <div className="typing-indicator">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
            )}
          </div>

          <div
            className="chat-input"
            style={{ display: "flex", padding: "10px" }}
          >
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Ask me anything..."
              style={{ flex: 1, marginRight: "5px" }}
            />
            <button className="send" onClick={sendMessage}>
              Send
            </button>
            <button className="clear" onClick={clearMessages}>
              Clear
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Chatbot;
