// pages/Projects.js
import React, { useState } from 'react';
import { FaTimes, FaGithub } from 'react-icons/fa';
import { projects } from '../data';

function ProjectModal({ project, isOpen, onClose }) {
  if (!isOpen || !project) return null;

  return (
    // semiâ€‘transparent backdrop
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-60">
      {/* modal window */}
      <div className="relative bg-gray-900 text-purple-100 rounded-lg shadow-xl max-w-2xl w-full mx-4 p-6">
        {/* close button */}
        <button
          onClick={onClose}
          className="absolute top-4 right-4 text-purple-300 hover:text-white"
        >
          <FaTimes size={24} />
        </button>

        {/* header */}
        <h2 className="text-2xl font-bold mb-4 text-center ">{project.title}</h2>

        {/*description */}
        {project.description && (
          <p className="mb-4">{project.description}</p>
        )}
         {/* functions */}
          {project.functions && (
            <>
              <h4 className="font-semibold mb-2 text-center">Main Functions</h4>
              <ul className="list-disc list-inside mb-4 text-sm">
                {project.functions.map((fn, idx) => (
                  <li key={idx}>{fn}</li>
                ))}
              </ul>
            </>
          )}


        {/* screenshots */}
        {project.images?.length > 0 && (
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mb-4">
            {project.images.map((src, i) => (
              <img
                key={i}
                src={src}
                alt={`${project.title} screenshot ${i + 1}`}
                className="rounded"
              />
            ))}
          </div>
        )}

        {/* tech stack */}
        <h4 className="font-semibold mb-2 text-center">Technologies</h4>
        <div className="flex flex-wrap gap-2 mb-4 justify-center">
          {project.technologies?.map((tech) => (
            <span
              key={tech.name}
              className="bg-purple-200 text-gray-900 px-2 py-1 rounded text-xs"
            >
              {tech.name}
            </span>
          ))}
        </div>

        {/* links */}
        <div className="flex gap-4 justify-center">
          {project.github && (
            <a
              href={project.github}
              target="_blank"
              rel="noopener noreferrer"
              className="inline-flex items-center"
            >
              <FaGithub className="mr-1" /> Code
            </a>
          )}
          {project.link && (
            <a
              href={project.link}
              target="_blank"
              rel="noopener noreferrer"
            >
              Live Demo
            </a>
          )}
        </div>
      </div>
    </div>
  );
}

export default function Projects() {
  const [activeTab, setActiveTab] = useState('completed');
  const [selectedProject, setSelectedProject] = useState(null);

  const completed = projects.filter((p) => p.status === 'completed');
  const school = projects.filter((p) => p.category === 'School');

  const handleOpen = (project) => setSelectedProject(project);
  const handleClose = () => setSelectedProject(null);

  return (
    <section id="projects" className="bg-gray-900 text-purple-100 p-10">
      {/* tabs */}
      <div className="flex justify-center mb-8">
        {['completed', 'school'].map((tab) => (
          <button
            key={tab}
            onClick={() => {
              setActiveTab(tab);
              setSelectedProject(null); // reset modal when switching tabs
            }}
            className={`mx-2 px-4 py-2 rounded-lg font-bold ${
              activeTab === tab
                ? 'bg-purple-200 text-gray-900'
                : 'bg-gray-800 text-purple-100'
            }`}
          >
            {tab === 'completed' ? 'Projects' : 'School'}
          </button>
        ))}
      </div>

      {/* project grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {(activeTab === 'completed' ? completed : school).map((project) => (
          <div
            key={project.slug}
            className="p-6 bg-gray-800 rounded-lg shadow-lg cursor-pointer hover:shadow-2xl transition-shadow"
            onClick={() => handleOpen(project)}
          >
            {/* category tag */}
            <span className="absolute top-3 right-3 bg-purple-200 text-gray-900 text-xs font-medium px-2 py-1 rounded-full">
              {project.category}
            </span>
            <h3 className="text-xl font-semibold mb-2">{project.title}</h3>
            <p className="text-sm text-purple-300">
              {project.subtitle ||
                `${project.description.substring(0, 80)}...`}
            </p>
          </div>
        ))}
      </div>

      {/* modal overlay */}
      <ProjectModal
        project={selectedProject}
        isOpen={!!selectedProject}
        onClose={handleClose}
      />
    </section>
  );
}
