import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

// Function to generate a smooth gradient from red (low skill) to green (high skill)
const getSkillColor = (level) => {
  const red = Math.max(255 - level * 2.55, 0); // Reduce red as skill increases
  const green = Math.min(level * 2.55, 255); // Increase green as skill increases
  return `rgb(${red}, ${green}, 80)`; // Smooth gradient from red → yellow → green
};

export default function SkillItem({ skillName, skillLevel }) {
  const [isHovered, setIsHovered] = useState(false);

  // Animate stroke offset for gauge
  const progressSpring = useSpring({
    strokeDashoffset: isHovered ? 125 - (125 * skillLevel) / 100 : 125, // Semi-circle range
    config: { tension: 200, friction: 15 },
  });

  return (
    <div
      className="relative flex flex-col items-center justify-center"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Skill Icon */}
      <div className="w-16 h-16 sm:w-20 sm:h-20 flex items-center justify-center rounded-full bg-gray-700 shadow-lg">
        {skillIcons[skillName] || <span className="text-white">{skillName}</span>}
      </div>

      {/* Skill Gauge (Semi-Circle) */}
      <svg width="100" height="50" viewBox="0 0 100 50">
        {/* Background Track */}
        <path
          d="M10,50 A40,40 0 0,1 90,50"
          stroke="#ddd"
          strokeWidth="8"
          fill="none"
        />
        {/* Animated Gauge */}
        <animated.path
          d="M10,50 A40,40 0 0,1 90,50"
          stroke={getSkillColor(skillLevel)}
          strokeWidth="8"
          fill="none"
          strokeDasharray="125"
          strokeDashoffset={progressSpring.strokeDashoffset}
          strokeLinecap="round"
        />
      </svg>

      {/* Skill Level Label */}
      {isHovered && (
        <span className="absolute bottom-[-20px] text-white text-sm font-semibold">
          {skillLevel}%
        </span>
      )}
    </div>
  );
}
