import React, { useState, useRef, useEffect } from "react";
import Chatbot from "./chatbot.js";
import './styles.css';

export default function About() {
  const [chatOpened, setChatOpened] = useState(false);
  const chatbotWrapperRef = useRef(null);

  useEffect(() => {
    const container = chatbotWrapperRef.current;
    if (container) {
      const handleClick = (e) => {
        if (e.target.closest('.chat-button')) {
          setChatOpened(true);
        } else if (e.target.closest('.close-btn')) {
          setChatOpened(false);
        }
      };
      container.addEventListener('click', handleClick);
      return () => container.removeEventListener('click', handleClick);
    }
  }, []);

  return (
    <section id="about" className="flex flex-col md:flex-row items-center justify-between min-h-screen px-10">
      {/* Left side - Chatbot + Text */}
      <div className="flex flex-col justify-center items-center md:items-start text-center md:text-left w-full md:w-1/2 space-y-4">
        {!chatOpened && (
          <p className="text-gray-700 text-lg leading-relaxed">
            Hello! My name is Annie. Have any questions?
          </p>
        )}
        <div ref={chatbotWrapperRef}>
          <Chatbot />
        </div>
      </div>

      {/* Right side - Image */}
      <div className="w-full md:w-1/2 flex justify-center md:justify-end">
        <div
          className="glass-effect overflow-hidden rounded-lg shadow-lg"
          style={{ width: "300px", height: "300px" }}
        >
          <img
            src="/Headshot.JPG"
            alt="Headshot"
            className="object-cover w-full h-full"
          />
        </div>
      </div>
    </section>
  );
}
