import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "./skillIcons"; // Import skill icons

// Function to determine skill level category
const getSkillCategory = (level) => {
  if (level <= 40) return "Beginner";
  if (level <= 75) return "Proficient";
  return "Master";
};

// Function to determine the number of dots based on skill level
const getDotCount = (level) => {
  if (level <= 40) return 1; // Beginner
  if (level <= 75) return 2; // Proficient
  return 3; // Master
};

// Function to determine dot colors for each proficiency level
const getDotColor = (index) => {
  const colors = ["#F44336", "#FFC107", "#4CAF50"]; // Red → Yellow → Green
  return colors[index]; // Assign color based on dot index
};

export default function SkillItem({ skillName, skillLevel }) {
  const [isHovered, setIsHovered] = useState(false);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });

  const category = getSkillCategory(skillLevel);
  const dotCount = getDotCount(skillLevel);

  // Animate the dots appearing in sequence
  const dotSpring = useSpring({
    opacity: isHovered ? 1 : 0,
    transform: isHovered ? "translateY(0px)" : "translateY(10px)", // Dots slide up on hover
    config: { tension: 200, friction: 20 },
  });

  return (
    <div
      className="relative flex flex-col items-center justify-center"
      onMouseEnter={() => setIsHovered(true)}
      onMouseMove={(e) => setMousePos({ x: e.clientX, y: e.clientY })}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Skill Icon */}
      <div className="w-16 h-16 sm:w-20 sm:h-20 flex items-center justify-center rounded-full bg-gray-700 shadow-lg">
        {skillIcons[skillName] || <span className="text-white">{skillName}</span>}
      </div>

      {/* Skill Meter (Dots Appear Below on Hover) */}
      {isHovered && (
        <animated.div className="absolute bottom-[-20px] flex space-x-2" style={dotSpring}>
          {Array.from({ length: dotCount }).map((_, index) => (
            <div
              key={index}
              className="w-3 h-3 rounded-full"
              style={{
                backgroundColor: getDotColor(index),
                animation: `fadeIn 0.3s ease ${index * 0.1}s forwards`,
                opacity: 0, // Prevents flashing
              }}
            />
          ))}
        </animated.div>
      )}

      {/* Skill Name Tooltip (Follows Cursor) */}
      {isHovered && (
        <div
          className="fixed bg-gray-900 text-white text-sm font-semibold px-3 py-1 rounded-lg shadow-lg pointer-events-none"
          style={{
            top: `${mousePos.y + 10}px`,
            left: `${mousePos.x + 15}px`,
            whiteSpace: "nowrap",
          }}
        >
          {skillName} ({category})
        </div>
      )}

      {/* CSS Keyframes for Smooth Dot Animations */}
      <style>
        {`
          @keyframes fadeIn {
            from { opacity: 0; transform: scale(0.5); }
            to { opacity: 1; transform: scale(1); }
          }
        `}
      </style>
    </div>
  );
}
