import React from "react";
import { useSpring, animated } from "@react-spring/web";
import skillIcons from "../skillIcons"; // Import skill icons

const experienceLevels = {
  JavaScript: 90,
  Python: 95,
  Java: 80,
  "C++": 60,
  HTML: 90,
  CSS: 85,
  Swift: 55,
  React: 85,
  Django: 80,
  "Unreal Engine": 75,
  Unity: 75,
  Blender: 50,
  AWS: 80,
  MySQL: 80,
  Postgres: 95,
  "Node.js": 85,
  Postman: 60,
  VR: 75,
  Git: 95,
  MongoDB: 80,
};

// Function to determine glow color based on experience level
const getGlowColor = (level) => {
  if (level >= 86) return "shadow-green-500"; // Advanced (Green Glow)
  if (level >= 61) return "shadow-yellow-500"; // Intermediate (Yellow Glow)
  return "shadow-red-500"; // Beginner (Red Glow)
};

// Function to determine experience label
const getExperienceLabel = (level) => {
  if (level >= 86) return "Advanced";
  if (level >= 61) return "Intermediate";
  return "Beginner";
};

export default function SkillItem({ skill }) {
  const progress = experienceLevels[skill] || 50; // Default to 50% if not listed

  // React Spring animations
  const iconSpring = useSpring({
    transform: "scale(1)",
    from: { transform: "scale(0.9)" },
    config: { tension: 200, friction: 15 },
  });

  const meterSpring = useSpring({
    width: `${progress}%`,
    from: { width: "0%" },
    config: { tension: 200, friction: 20 },
  });

  return (
    <div className="group flex flex-col items-center">
      {/* Circular Icon with Glow on Hover */}
      <div
        className={`relative w-24 h-24 flex items-center justify-center rounded-full bg-gray-700 transition-all duration-300 group-hover:${getGlowColor(
          progress
        )} group-hover:shadow-lg`}
      >
        <animated.div
          style={iconSpring}
          className="group-hover:scale-110 transition-transform duration-300"
        >
          {skillIcons[skill] || <span className="text-white">{skill}</span>}
        </animated.div>
      </div>

      {/* Skill Name */}
      <span className="mt-2 text-white">{skill}</span>

      {/* Progress Meter with Labels */}
      <div className="relative w-28 h-2 bg-gray-600 rounded-full mt-2 overflow-hidden">
        <animated.div
          className="h-2 bg-green-500 rounded-full"
          style={meterSpring}
        />
      </div>

      {/* Labels for Beginner, Intermediate, Advanced */}
      <div className="flex justify-between w-28 text-xs text-gray-400 mt-1">
        <span>Beginner</span>
        <span>Intermediate</span>
        <span>Advanced</span>
      </div>

      {/* Experience Level Label */}
      <span
        className={`text-sm font-medium mt-2 ${
          progress >= 86
            ? "text-green-400"
            : progress >= 61
            ? "text-yellow-400"
            : "text-red-400"
        }`}
      >
        {getExperienceLabel(progress)}
      </span>
    </div>
  );
}
